var errMsg = null;
var tokenError = msg.error;
if (tokenError != undefined)
{
    errMsg = 
    {
        topic : msg.topic, 
        payload:
        {
            topic:'badToken', 
            payload: msg.topic + " badToken",
            userID : msg.userID,
            role   : msg.role
        }
    };
    return [null,errMsg];
}
if (msg.token.expDate > 0)
{
    if (msg.token.expDate < new Date().getTime())
    {
        errMsg = 
        {
            topic : msg.topic, 
            payload:
            {
                topic:'loginExpired', 
                payload: msg.topic + " login expired",
                userID : msg.userID,
                role   : msg.role
            }
        };
        return [null,errMsg];
    }
}
let permittedRole = false;
for (let itoken = 0; itoken < msg.token.roles.length; ++itoken)
{
    if (msg.token.roles[itoken] == msg.role) permittedRole = true;
}
if (permittedRole) return [msg,null];

errMsg = 
{
    topic : msg.topic, 
    payload:
    {
        topic  : 'permissionError', 
        payload: msg.topic + " not permitted",
        userID : msg.userID,
        role   : msg.role
    }
};

return [null,errMsg];

template:
$( document ).ready(function()
{
    setTimeout(function(){location.replace("{{{payload.httpsUrl}}}");}, 3000);
});

func:
if (Number(env.get('REQUIREHTTPS')) < 1 ) return [msg,null];
let ireq = -1;
for (let ii = 0; ii < msg.req.rawHeaders.length; ++ii)
{
    if (msg.req.rawHeaders[ii] == "X-Forwarded-Proto") ireq = ii + 1;
}
if (ireq >= 0) 
{
    if (msg.req.rawHeaders[ireq] == 'http')
    {
        msg.cookies = { };
        let ireq2 = -1;
        for (let ii = 0; ii < msg.req.rawHeaders.length; ++ii)
        {
            if (msg.req.rawHeaders[ii] == "Host") ireq2 = ii + 1;
        }
        if (ireq2 >= 0)
        {
            let host = msg.req.rawHeaders[ireq2];
            let url = "https://" + host + msg.req.url;
            msg.payload['httpsUrl'] = url;
        }
        else
        {
            msg.payload['httpsUrl'] = '';
        }
        msg.payload.errorMsg = "Insecure Connection!";
        return [null,msg];
    }
}
return [msg,null];

let app = msg.req['_parsedUrl'].pathname.split('/')[1];
global.set(app, msg.payload.trayUpdateFilter);
msg['customCard'] = {domSetup:"",classMethods:""}
for (let icard in msg.payload.cards)
{
  if (msg.payload.cards[icard].type == "CustomCard")
  {
    let fs = global.get('fs');
    try 
    {
      msg.customCard.domSetup = fs.readFileSync(env.get('HTMLSTATICDIR') + '/customApps/' + msg.payload.cards[icard].config.customCodeFolder + '/domSetup.js', 'utf8');
    } 
    catch (err) 
    {
      msg.customCard.domSetup = "";
    }
    try 
    {
      msg.customCard.classMethods = fs.readFileSync(env.get('HTMLSTATICDIR') + '/customApps/' + msg.payload.cards[icard].config.customCodeFolder + '/classMethods.js', 'utf8');
    } 
    catch (err) 
    {
      msg.customCard.classMethods = "";
    }
//    let b = Buffer.from(msg.payload.cards[icard].config.domSetup, 'base64');
//    msg.customCard.domSetup = b.toString();
//    let b = Buffer.from(msg.payload.cards[icard].config.classMethods, 'base64');
//    msg.customCard.classMethods = b.toString();
  }
}
let config = JSON.stringify(msg.payload);
let trays = [];
for (let it = 0; it < msg.payload.trayTypes.length; ++it)
{
  let tray = { type: msg.payload.trayTypes[it], name: msg.urlInput.trayNames[it]};
  trays.push(tray);
}
let urlInput = JSON.stringify({ box: msg.urlInput.box, trayList: trays});
delete msg.urlInput;

msg.payload = { config: config, urlInput: urlInput, title:msg.payload.title, allowedRoles : msg.payload.allowedRoles, userID:getRandomInt(32768)};
return msg;
function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}

let app = msg.req['_parsedUrl'].pathname.split('/')[1];
msg['urlInput'] = {};
msg['urlInput']['box'] = global.get('box');
msg['urlInput']['trayNames'] = msg.payload.trayNames.split(',');
let external = false;
if ("external" in msg.payload) external = true;
msg.payload = [{ app: app},{projection  :{_id : 0} }]

if (external)
{
    return [null,msg];
}
return [msg,null];

template:
//onOffSet
        class OnOffSetCube extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {

                this.settingButton = document.createElement('button');
                this.settingButton.setAttribute('class', 'btn btn-block onOffSetCube-button');
                this.settingButton.innerHTML = '';
                this.settingButton.onclick = event => {this.toggle()};
                widgetColumns[0].appendChild(this.settingButton);
            }
            toggle()
            {
                let limits = [0,1];
                if ('limits' in this.config)
                {
                    limits[0] = this.config.limits[0];
                    limits[1] = this.config.limits[1];
                }
                let onOff = limits[0];
                if (this.tray[this.config.cubeName].value <= limits[0])
                {
                    onOff = limits[1];
                }
                else
                {
                    onOff = limits[0];
                }
                this.settingButton.setAttribute('style','background: var(--color2); color: var(--color2);');
                let payload = {'cube': this.config.cubeName, 'value':onOff};
                let actionMsg = 
                    {
                        topic   : this.card.app.box + '/tray/' +  this.tray.type + '/' +  this.tray.name + '/setting/setting',
                        payload : payload
                    };
                this.card.app.sendActionMsg('setting','setting',actionMsg);
            }
            updateCubeValue()
            {
                if (this.tray[this.config.cubeName].hasOwnProperty('enabled') )
                {
                    if (this.tray[this.config.cubeName].enabled == 1)
                    {
                        this.settingButton.disabled = false;
                    }
                    else
                    {
                        this.settingButton.disabled = true;
                    }
                }

                let limits = [0,1];
                if ('limits' in this.config)
                {
                    limits[0] = this.config.limits[0];
                    limits[1] = this.config.limits[1];
                }
                if (this.tray[this.config.cubeName].value <= limits[0])
                {
                    if ('onOffLabel' in this.config)
                    {
                        this.settingButton.innerHTML = this.config.onOffLabel[0];
                    }
                    else
                    {
                        this.settingButton.innerHTML = "Off";
                    }

                    this.settingButton.setAttribute('style','background:' + this.config.offColor + '; color: black;');
                    return;
                }
                if (this.tray[this.config.cubeName].value >= limits[1]) 
                {   
                    if ('onOffLabel' in this.config)
                    {
                        this.settingButton.innerHTML = this.config.onOffLabel[1];
                    }
                    else
                    {
                        this.settingButton.innerHTML = "On";
                    }
                    this.settingButton.setAttribute('style','background:' + this.config.onColor + '; color: black;');
                    return;
                }
                this.settingButton.innerHTML = "?";
                let middleColor = "grey";
                if ('middleColor' in this.config) middleColor = this.config.middleColor;
                this.settingButton.setAttribute('style','background:' + middleColor + '; color: black;');
                return;
            }
        }

