//numberSetCube
        class NumberSetCube   extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {
                this.startingValueText = "0";
                this.inputBox = document.createElement('input');
                this.inputBox.setAttribute('class', 'numberSetCube-inputbox');
                this.inputBox.setAttribute('type', 'text');
                this.inputBox.setAttribute('value', '0');
                this.inputBox.oninput = event => {this.inputChange()};
                widgetColumns[0].appendChild(this.inputBox);

                this.unitSpan  = document.createElement('span');
                this.unitSpan.setAttribute('class', 'cubeRowParameterView-unit-span');
                this.unitSpan.innerHTML = '';
                widgetColumns[1].appendChild(this.unitSpan);

                this.settingButton = document.createElement('button');
                this.settingButton.setAttribute('class', 'btn btn-block numberSetCube-button');
                this.settingButton.innerHTML = '&#10003;';
                this.settingButton.onclick = event => {this.changeSetting()};
                widgetColumns[2].appendChild(this.settingButton);

                this.showSettingButton(false);
            }

            showSettingButton(showButton)
            {
                if (showButton)
                {
                    this.settingButton.style.display='block';
                }
                else
                {
                    this.settingButton.style.display='none';
                }
            }
            inputChange()
            {
                this.changed = true;
                this.showSettingButton(true);
            }
            changeSetting()
            {
                this.showSettingButton(false);
                if (isNaN(this.inputBox.value))
                {
                    this.changed = false;
                    return;
                }
                let lolo = this.tray[this.config.cubeName].alarm.limits.lolo;
                let hihi = this.tray[this.config.cubeName].alarm.limits.hihi;
                let newSetting = Number(this.inputBox.value);
                if ((newSetting <= lolo) || (newSetting >= hihi))
                {
                    this.inputBox.value = this.startingValueText;
                    this.card.app.acknowledgeDialog.showDialog('Error: Setting outside range',() => {});
                }
                let payload = {'cube': this.config.cubeName, 'value':Number(this.inputBox.value)};
                let actionMsg = 
                    {
                        topic   : this.card.app.box + '/tray/' +  this.tray.type + '/' +  this.tray.name + '/setting/setting',
                        payload : payload
                    };
                this.card.app.sendActionMsg('setting','setting',actionMsg);
                this.changed = false;
            }
            updateCubeValue()
            {
                if (this.tray[this.config.cubeName].hasOwnProperty('enabled') )
                {
                    if (this.tray[this.config.cubeName].enabled == 1)
                    {
                        this.inputBox.disabled = false;
                    }
                    else
                    {
                        this.inputBox.disabled = true;
                    }
                }
                if (!this.changed) 
                {
                    if ( "round" in this.config)
                    {
                        this.inputBox.value = this.tray[this.config.cubeName].value.toFixed(this.config.round);
                    }
                    else
                    {
                        this.inputBox.value = this.tray[this.config.cubeName].value.toString();
                    }
                    this.startingValueText = this.inputBox.value;
                    this.unitSpan.innerHTML    = this.tray[this.config.cubeName].unit;
                }

            }
        }

// cubeControlApp
        class CubeControlApp 
        {
            constructor(trays, config, userID)
            {
                this.box = trays.box;
                this.trays = trays.trayList;
                this.config = config;
                this.ws = null;
                this.userID = userID;
                this.wsUri = '';
                this.wsConnected = false;
                this.connectionTrys = 0;
                this.route = '';
                this.navBar = null;
                this.alarmLimitDialog = new AlarmLimitDialog(this);
                this.cubeCards = [];
//                this.iframeCards = [];
                this.cardOrder = [];
                this.archiveCard = null;
                this.appConnectedOnce = false;
                this.traysLoaded = false;
                this.xyPlotCard = [];
                this.timePlotCard = null;
                this.barPlotCard = [];
                this.vectorPlotCard = [];
                this.customCard = null;
                this.userCard = null;
                this.messageDialog     = new OkCancelDialog(this, 'Message', 0);
                this.acknowledgeDialog = new OkCancelDialog(this, 'Acknowledge', 1);
                this.okCancelDialog    = new OkCancelDialog(this, 'Acknowledge', 2);

                if ("navbar" in this.config)
                {
                    this.navBar = new (dict.get('NavBar'))(this,this.config.navbar.config);
                }

                for (let icard = 0; icard < config.cards.length; ++icard)
                {
                    switch (this.config.cards[icard].type)
                    {
                        case 'CubeRowCard':
                            let cubeCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config);
                            this.cubeCards.push(cubeCard);
                            this.cardOrder.push(cubeCard);
                            break;
                        case 'CustomCard':
                            let customCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config);
                            this.customCard = customCard;
                            this.cardOrder.push(customCard);
                            break;
                        case 'ArchivePlotCard':
                            let archiveCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config);
                            this.archiveCard = archiveCard;
                            this.cardOrder.push(this.archiveCard);
                            break;
                        case 'TimePlotCard':
                            let timePlotCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config);
                            this.timePlotCard = timePlotCard;
                            this.cardOrder.push(this.timePlotCard);
                            break;
                        case 'XyPlotCard':
                            let xyPlotCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config, this.xyPlotCard.length);
                            this.xyPlotCard.push(xyPlotCard);
                            this.cardOrder.push(xyPlotCard);
                            break;
                        case 'VectorPlotCard':
                            let vectorPlotCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config, this.vectorPlotCard.length);
                            this.vectorPlotCard.push(vectorPlotCard);
                            this.cardOrder.push(vectorPlotCard);
                            break;
                        case 'BarPlotCard':
                            let barPlotCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config, this.barPlotCard.length);
                            this.barPlotCard.push(barPlotCard);
                            this.cardOrder.push(barPlotCard);
                            break;
                        case 'IframeCard':
                            let iframeCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config);
//                            this.iframeCards.push(iframeCard);
                            this.cardOrder.push(iframeCard);
                            break;
                        case 'UserCard':
                            let userCard = new (dict.get(this.config.cards[icard].type))(this, this.config.cards[icard].config);
                            this.userCard = userCard;
                            this.cardOrder.push(this.userCard);
                            break;
                        default:
                            break;
                    }
                }
            }
            webSocketConnected()
            {
                return this.wsConnected;
            }
            wsConnectC()
            {
                if (this.wsUri.length < 1)
                {
                    let uri = window.location.href.split('://');
                    let wslead = 'ws://';
                    if (uri[0] == 'https') wslead = 'wss://';
                    let questionLocation = uri[1].indexOf('?');
                    if (questionLocation >= 0)
                    {
                        uri[1] = uri[1].substring(0,questionLocation);
                    }
                    this.route = uri[1].split('/')[1];
                    if (uri[1].indexOf('/') < (uri[1].length - 1))
                    {
                        this.wsUri = wslead + uri[1] + '/websocket';
                    }
                    else
                    {
                        this.wsUri = wslead + uri[1] + 'websocket';
                    }
                }

                this.ws = new WebSocket(this.wsUri);
                this.ws.onmessage  = event => {this.onWebSocketMessage(JSON.parse(event.data))};
                this.ws.onopen     = event => 
                {
                    this.onWebsocketOpen();
                };
                this.ws.onclose    = event => 
                {
                    this.connectionTrys = this.connectionTrys + 1;
                    console.log("Websocket closed");
                    this.wsConnected = false;
                    if (this.connectionTrys < 20000)
                    {
                        console.log('Trying to reconnect...');
                        setTimeout(()=>this.wsConnectC(),2000);
                    }
                    else
                    {
                        this.acknowledgeDialog.showDialog('Error: Websocket disconnect.',() => 
                        {
                            this.connectionTrys = 0;
                            console.log('Trying to reconnect...');
                            setTimeout(()=>this.wsConnectC(),2000);
                        });
                    }
                };
            }
            onWebsocketOpen()
            {
                console.log("Websocket connected");
                this.wsConnected = true;
                if (!this.appConnectedOnce) this.readTraysFromDatabase();
                if (!this.appConnectedOnce && (this.archiveCard != null)) this.archiveCard.requestArchiveData();
                this.appConnectedOnce = true;
            }
            onWebSocketMessage(msg)
            {
                let trayIndex = -1;
                switch(msg.topic)
                {
                    case 'getTraysFromDatabase':
                        if (msg.userID != this.userID) return;
                        trayIndex = -1;
                        for (let itray = 0; itray < this.trays.length; ++ itray)
                        {
                            for (let ipay = 0; ipay < msg.payload.length; ++ ipay)
                            {
                                if ((msg.payload[ipay].type == this.trays[itray].type) && (msg.payload[ipay].name == this.trays[itray].name) )
                                {
                                    this.trays[itray] = JSON.parse(JSON.stringify(msg.payload[ipay]));
                                    trayIndex = itray;
                                    for (let ic = 0; ic < this.cubeCards.length; ++ic)
                                    {
                                        this.cubeCards[ic].trayUpdate(this.trays[trayIndex], trayIndex);
                                    }
                                    for (let ic = 0; ic < this.xyPlotCard.length; ++ic)
                                    {
                                        this.xyPlotCard[ic].addTracePointsToXyPlot(trayIndex);
                                    }
                                    for (let ic = 0; ic < this.vectorPlotCard.length; ++ic)
                                    {
                                        this.vectorPlotCard[ic].addVectorsToVectorPlot(trayIndex);
                                    }
                                    for (let ic = 0; ic < this.barPlotCard.length; ++ic)
                                    {
                                        this.barPlotCard[ic].addTracePointsToBarPlot(trayIndex);
                                    }
                                    if (this.timePlotCard != null) this.timePlotCard.addTracePointsToTimePlot(trayIndex);
                                }
                            }
                        }
                        if (trayIndex >= 0)
                        {
                            if (this.customCard != null) this.customCard.trayUpdate(this.trays);
                        }
                        this.traysLoaded = true;
                        break;
                    case 'mqttUpdate':
                        if (!this.traysLoaded) return;
                        trayIndex = -1;
                        for (let itray = 0; itray < this.trays.length; ++itray) 
                        {
                            if ((msg.payload.type == this.trays[itray].type) && (msg.payload.name == this.trays[itray].name)) 
                            {
                                this.trays[itray] = JSON.parse(JSON.stringify(msg.payload));
                                trayIndex = itray;
                            }
                        }
                        if (trayIndex < 0) break;
                        for (let ic = 0; ic < this.cubeCards.length; ++ic) 
                        {
                            this.cubeCards[ic].trayUpdate(this.trays[trayIndex], trayIndex);
                        }
                        for (let ic = 0; ic < this.xyPlotCard.length; ++ic)
                        {
                            this.xyPlotCard[ic].addTracePointsToXyPlot(trayIndex);
                        }
                        for (let ic = 0; ic < this.vectorPlotCard.length; ++ic)
                        {
                            this.vectorPlotCard[ic].addVectorsToVectorPlot(trayIndex);
                        }
                        for (let ic = 0; ic < this.barPlotCard.length; ++ic)
                        {
                            this.barPlotCard[ic].addTracePointsToBarPlot(trayIndex);
                        }
                        if (this.timePlotCard != null) this.timePlotCard.addTracePointsToTimePlot(trayIndex);
                        if (this.customCard != null) this.customCard.trayUpdate(this.trays);
                       break;
                    case 'readDatabase':
                        if (msg.userID != this.userID) return;
                        let data = JSON.parse(JSON.stringify(msg.payload));
                        if (this.archiveCard != null) this.archiveCard.putArchiveValues(data);
                        break;
                    case 'customReadArchive':
                        if (msg.userID != this.userID) return;
                        let archiveData = JSON.parse(JSON.stringify(msg.payload));
                        if (this.customCard != null) this.customCard.putCustomReadArchive(archiveData);
                        break;
                    case 'renew':
                        if (msg.userID != this.userID) return;
                        this.updateCookie(msg);
                        break;
                    case 'permissionError':
                        if (msg.userID != this.userID) return;
                        this.acknowledgeDialog.showDialog('Error: You do not have permission.',function(){});
                        break;
                    case 'loginExpired':
                        if (msg.userID != this.userID) return;
                         location.reload();
                        break;
                    default:
                        break;
                }
            }
            handleArchiveCardClick(clickData)
            {
                this.customCard.handleArchiveCardClick(clickData);
            }
            docReady()
            {
                if ("multiColumns" in this.config)
                {
                    if (this.config.multiColumns)
                    {
                        let appDiv = document.getElementById('appContent');
                        appDiv.classList.add("card-columns");
                        appDiv.classList.add("custom-columns");
                    }
                }

                if (this.navBar != null) this.navBar.onDocumentReady();
                this.alarmLimitDialog.onDocumentReady("alarmLimitCardContainer");
                for (let ic = 0; ic < this.cardOrder.length; ++ic)
                {
                    this.cardOrder[ic].onDocumentReady();
                }
/*
                for (let ic = 0; ic < this.cubeCards.length; ++ic)
                {
                    this.cubeCards[ic].onDocumentReady();
                }
                if (this.archiveCard != null) this.archiveCard.onDocumentReady();
                if (this.timePlotCard != null) this.timePlotCard.onDocumentReady();
                if (this.userCard != null) this.userCard.onDocumentReady();
*/
                this.messageDialog.onDocumentReady('messageCardContainer');
                this.acknowledgeDialog.onDocumentReady('acknowledgeCardContainer');
                this.okCancelDialog.onDocumentReady('okCancelCardContainer');

                this.wsConnectC();
            }
            readTraysFromDatabase()
            {
                let queryArray = [];
                for (let ii = 0; ii < this.trays.length; ++ii)
                {
                    queryArray[ii] = {$and : [ {type: this.trays[ii].type}, {name: this.trays[ii].name} ]};
                }
                let actionMsg = { $or : queryArray };
                this.sendActionMsg('getTraysFromDatabase','readDatabase', actionMsg);
            }
            getCookie(extension)
            {
                let cookies = document.cookie.split(';');
                let token = null;
                let cookieName = this.box + extension + "=";
                for (let icookie = 0; icookie < cookies.length; ++icookie)
                {
                    let index = cookies[icookie].indexOf(cookieName);
                    if (index >= 0)
                    {
                        token = cookies[icookie].substring(index + cookieName.length);
                    }
                }
                return token;
            }
            updateCookie(msg)
            {
                let expTimeout = Math.round((Number(msg.expDate) - new Date().getTime()) / 1000).toString();
                if (Number(msg.expDate) < 0)
                {
                    document.cookie = this.box + "Role=" + msg.role;
                    document.cookie = this.box + "ExpDate=" + msg.expDate;
                    document.cookie = this.box + "Username=" + msg.username;
                }
                else
                {
                    let expDateString = "; max-age=" + expTimeout;
                    document.cookie = this.box + "Role=" + msg.role + expDateString;
                    document.cookie = this.box + "ExpDate=" + msg.expDate + expDateString;
                    document.cookie = this.box + "Username=" + msg.username + expDateString;
                }
            }
            sendActionMsg(topic,role,actionMsg)
            {
                let roleToken = "";
                roleToken  = this.getCookie('Role');
                if (roleToken == null)
                {
                    location.reload();
                    return;
                }
                let webSocketMsg = 
                {
                    topic     : topic,
                    payload   : actionMsg,
                    route     : this.route,
                    userID    : this.userID,
                    token     : this.getCookie('Role'),
                    role      : role
                };
                this.ws.send(JSON.stringify(webSocketMsg));
            }
        }

