//cubeRowParameterView
        class CubeRowParameterView 
        {
            constructor(card, config)
            {
                this.card = card;
                this.config = JSON.parse(JSON.stringify(config));
            }

            onDocumentReady(parentElement)
            {
                this.parentElement = parentElement;
                let inLineTable = document.createElement('table');
                inLineTable.setAttribute('width', '100%');
                let inLineRow  = document.createElement('tr');
                this.labelColumn  = document.createElement('td');
                this.labelColumn.setAttribute('class', 'cubeRowParameterView-label-column');

                let labelSpan  = document.createElement('span');
                if ('cubeLabel' in this.config)
                {
                    labelSpan.innerHTML = this.config.cubeLabel;
                }
                else
                {
                    labelSpan.innerHTML = this.card.app.trays[this.config.trayIndex].type + "." + this.card.app.trays[this.config.trayIndex].name + "." + this.config.cubeName;
                }
                this.labelColumn.appendChild(labelSpan);
                inLineRow.appendChild(this.labelColumn);

                let widgetColumns = [];
                for (let iw = 0; iw < 3; ++iw)
                {
                    let iwcol  = document.createElement('td');
                    iwcol.setAttribute('class', 'cubeRowParameterView-widget-column-' + (iw + 1).toString());
                    inLineRow.appendChild(iwcol);
                    widgetColumns.push(iwcol);
                }

                this.addComponents(widgetColumns);

                let alarmColumn  = document.createElement('td');
                alarmColumn.setAttribute('class', 'cubeRowParameterView-alarmConfig-column');
                this.alarmButton = document.createElement('input');
                this.alarmButton.setAttribute('type', 'image');
                this.alarmButton.setAttribute('src', '/img/greenLight.png');
                this.alarmButton.setAttribute('class', 'cubeRowParameterView-alarmConfig-button');
                this.alarmButton.onclick = event => {this.changeAlarmConfig()};
                alarmColumn.appendChild(this.alarmButton);
                inLineRow.appendChild(alarmColumn);

                inLineTable.appendChild(inLineRow);
                parentElement.appendChild(inLineTable);
            }

            trayUpdate(tray, trayIndex)
            {
                if (trayIndex != this.config.trayIndex) return;
                this.tray = tray;
                this.updateCubeValue();
                switch(this.tray[this.config.cubeName].alarm.value)
                {
                    case 0:
                        this.alarmButton.src="/img/greenLight.png";
                        break;
                    case 1:
                        this.alarmButton.src="/img/blueLight.png";
                        break;
                    case 2:
                        this.alarmButton.src="/img/yellowLight.png";
                        break;
                    case 3:
                        this.alarmButton.src="/img/purpleLight.png";
                        break;
                    case 4:
                        this.alarmButton.src="/img/redLight.png";
                        break;
                    default:
                        this.alarmButton.src="/img/greyLight.png";
                        break;

                }
            }
            changeAlarmConfig()
            {
                this.card.app.alarmLimitDialog.editAlarmConfig(this.config.cubeName, this.tray);
            }

        }

