            {
                if (this.tray[this.config.cubeName].value != newState)
                {
                    let payload = {'cube': this.config.cubeName, 'value':newState};
                    let actionMsg = 
                        {
                            topic   : this.card.app.box + '/tray/' +  this.tray.type + '/' +  this.tray.name + '/setting/setting',
                            payload : payload
                        };
                    this.card.app.sendActionMsg('setting','setting',actionMsg);
                }
            }
            updateCubeValue()
            {
                if (this.tray[this.config.cubeName].hasOwnProperty('enabled') )
                {
                    if (this.tray[this.config.cubeName].enabled == 1)
                    {
                        for (let ib in this.stateButtons) this.stateButtons[ib].disabled = false;
                    }
                    else
                    {
                        for (let ib in this.stateButtons) this.stateButtons[ib].disabled = true;
                    }
                }
                for (let ib = 0; ib < this.config.states.length; ++ib)
                {
                    if (this.tray[this.config.cubeName].value == this.config.states[ib].value)
                    {
                        this.stateButtons[ib].setAttribute('style','background:' + this.config.states[ib].color + ';color: black;');
                    }
                    else
                    {
                        this.stateButtons[ib].setAttribute('style','background:grey;color: black;');

                    }
                }
            }

        }

