            customTimeButtonClicked()
            {
                if (this.app.getCookie('Username') == "external")
                {
                    this.app.acknowledgeDialog.showDialog("You do not have permission",function(){});
                }
                else
                {
                    this.requestArchiveData();
                }
            }
            requestArchiveData()
            {

                let startDate = new Date(this.startDateInputWidget.value).getTime();
                let stopDate  = new Date(this.stopDateInputWidget.value).getTime();
                if ((stopDate - startDate) < 1 ) return;

                let bigTimeSpan = 2 * 168 * 3600000;
                if (this.config.timeUnit == 'day') bigTimeSpan = 24 * bigTimeSpan; 

                if ((stopDate - startDate)  > bigTimeSpan)
                {
                    this.app.okCancelDialog.showDialog('Warning: Long time period. Continue?',(isOk) => 
                    {
                        if (isOk) this.prepareCustomTimeArchiveRequest();
                    });
                }
                else
                {
                    this.prepareCustomTimeArchiveRequest();
                }

            }
            prepareCustomTimeArchiveRequest()
            {
                let startDate = new Date(this.startDateInputWidget.value).getTime();
                let stopDate  = new Date(this.stopDateInputWidget.value).getTime();
                this.startDateInputWidget.disabled = true;
                this.stopDateInputWidget.disabled = true;
                this.getDataButtonWidget.disabled = true;


                this.archiveRequestList = [];
                this.archiveList = [];
            // If we are replacing a previously generated file we need to
            // manually revoke the object URL to avoid memory leaks.
                for (let ii in this.csvFile)
                {
                    if (this.csvFile[ii] !== null) 
                    {
                        window.URL.revokeObjectURL(this.csvFile[ii]);
                    }
                }
                this.csvFile = [];
                if (this.csvLinkTable.hasChildNodes())
                {
                    while (this.csvLinkTable.firstChild) 
                    {
                        let csvLinkTableR = this.csvLinkTable.lastChild;
                        while(csvLinkTableR.firstChild)
                        {
                            let csvLinkTableRC = csvLinkTableR.lastChild;
                            while (csvLinkTableRC.firstChild)
                            {
                                csvLinkTableRC.removeChild(csvLinkTableRC.lastChild);
                            }
                            csvLinkTableR.removeChild(csvLinkTableR.lastChild);
                        }
                        this.csvLinkTable.removeChild(this.csvLinkTable.lastChild);
                    }
                }

                for (let itray = 0; itray < this.app.trays.length; ++itray)
                {
                    let cubeNameList = [];
                    for (let icube = 0; icube < this.config.cubeList[itray].length; ++icube) 
                        cubeNameList.push(this.config.cubeList[itray][icube].name);
                    this.archiveRequestList[itray] = 
                    {
                        type : this.app.trays[itray].type,
                        name : this.app.trays[itray].name, 
//need to test what happens when cubeList is empty
                        cube : cubeNameList,
                        startDate: startDate, 
                        stopDate: stopDate,
                    };

                }
                this.getArchiveValues();
            }
            getArchiveValues()
            {
                let actionMsg = 
                {
                    tray      : this.archiveRequestList[0]
                };
                this.app.sendActionMsg('readDatabase','readDatabase', actionMsg);
                this.archiveRequestList.shift();
                return;
            }
            putArchiveValues(data)
            {
                this.archiveList.push(data);
                if (this.archiveRequestList.length > 0)
                {
                    this.getArchiveValues();
                }
                else
                {
                    this.startDateInputWidget.disabled = false;
                    this.stopDateInputWidget.disabled = false;
                    this.getDataButtonWidget.disabled = false;
                    this.printCsvTable()
                    this.plotArchive();
                }
            }
            toggle()
            {

                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                    if (this.archivePlotTraces.length > 0)
                    {
                        this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                        Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                        this.archiveChartDiv.on('plotly_click', data => {this.getClickData(data)});
                    }
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            toggle2()
            {

                if (this.cardBodyDiv2.style.display === "none") 
                {
                    this.cardBodyDiv2.style.display = "block";
                    this.displayButton2.innerHTML = 'Close';
                } 
                else 
                {
                    this.cardBodyDiv2.style.display = "none";
                    this.displayButton2.innerHTML = 'Open';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.archivePlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    this.archiveChartDiv.on('plotly_click', data => {this.getClickData(data)});
                }
            }
            plotArchive()
            {
                this.archivePlotTraces = [];
                for (let itray = 0; itray < this.archiveList.length; ++itray)
                {
                    if (this.archiveList[itray].trace != null)
                    {
                        for (let icube = 0; icube < this.archiveList[itray].tray.cube.length; ++icube)
                        {
                            let traceType = 'scatter';
                            if ("type" in this.config.cubeList[itray][icube]) 
                            {
                                traceType = this.config.cubeList[itray][icube].type;
                            }
                            let lineMode = 'lines';
                            let markerSize = 12;
                            if ("mode" in this.config.cubeList[itray][icube]) lineMode = this.config.cubeList[itray][icube].mode;
                            if ("size" in this.config.cubeList[itray][icube]) markerSize = this.config.cubeList[itray][icube].size;
                            let nameLabel = this.archiveList[itray].tray.type + '.' + this.archiveList[itray].tray.name + '.' + this.archiveList[itray].tray.cube[icube];
                            if ("label" in this.config.cubeList[itray][icube]) nameLabel = this.config.cubeList[itray][icube].label;
                            let shape = "circle"
                            if ("symbol" in this.config.cubeList[itray][icube]) shape = this.config.cubeList[itray][icube].symbol;
                            let trace =
                            {
                                x: [],
                                y: [],
                                name: nameLabel,
                                yaxis: this.config.cubeList[itray][icube].yaxis,
                                type: traceType,
                                mode: lineMode,
                                marker: { size: markerSize, symbol:shape },
                                line: { color: this.config.cubeList[itray][icube].color }
                            };
                            for (let ipt = 0; ipt < this.archiveList[itray].trace.length; ++ipt)
                            {
                                trace.x.push(this.archiveList[itray].trace[ipt].timeStamp);
                                trace.y.push(this.archiveList[itray].trace[ipt][this.archiveList[itray].tray.cube[icube]]);
                            }
                            this.archivePlotTraces.push(trace);
                            this.curveList.push({itray:itray, icube:icube});
                        }
                    }
                }
                if (this.archivePlotTraces.length > 0)
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    this.archiveChartDiv.on('plotly_click', data => {this.getClickData(data)});
                }
            }
            getClickData(data)
            {
                let clickData = [];
                for (let icurve in data.points)
                {
                    let itray = this.curveList[data.points[icurve].curveNumber].itray;
                    let icube = this.curveList[data.points[icurve].curveNumber].icube;
                    let trayType = this.archiveList[itray].tray.type;
                    let trayName = this.archiveList[itray].tray.name;
                    let trayCube = this.archiveList[itray].tray.cube[icube];
                    let timeStamp = this.archiveList[itray].trace[data.points[icurve].pointIndex].timeStamp;
                    let value = this.archiveList[itray].trace[data.points[icurve].pointIndex][trayCube];
                    clickData.push({type:trayType,name:trayName,cube:trayCube,timeStamp:timeStamp,value:value});
                }
                this.app.handleArchiveCardClick(clickData)
            }
            printCsvTable()
            {
                for (let itrace in this.archiveList)
                {
                    if (this.archiveList[itrace].trace != null)
//                    if (this.archiveList[itrace].trace.length > 0 )
                    {
                        let csvLinkTableR = document.createElement('tr');
                        this.csvLinkTable.appendChild(csvLinkTableR);
                        let csvLinkTableRC = document.createElement('td');
                        csvLinkTableR.appendChild(csvLinkTableRC);
                        csvLinkTableRC.style.width = "100%";

                        let csvLinkTableRCA =  document.createElement('a');
                        let href = this.makeCsvFile(itrace);
                        this.csvFile.push(href);
                        csvLinkTableRCA.href = href;
                        csvLinkTableRCA.target = '_blank';
                        csvLinkTableRCA.download = this.archiveList[itrace].tray.type + '_' + this.archiveList[itrace].tray.name + '.csv';
                        csvLinkTableRCA.classList.add('cubeListChooser-csv-linked');
                        csvLinkTableRCA.style.textDecoration = 'underline';
                        csvLinkTableRCA.innerHTML = this.archiveList[itrace].tray.type + '_' + this.archiveList[itrace].tray.name + '.csv';
                        csvLinkTableRC.appendChild(csvLinkTableRCA);
                    }
                }

            }
            makeCsvFile(itrace)
            {
                let dataString = '';
                let startT = this.archiveList[itrace].trace[0].timeStamp;
                let startTString = new Date(startT).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', '');


                dataString = dataString + 'Tray,' + this.archiveList[itrace].tray.type + ',' + this.archiveList[itrace].tray.name +'\n';
                dataString = dataString + 'StartDate,' + startTString + '\n';
                dataString = dataString + 'StartDate (mS),' + startT.toString() + '\n';
                dataString = dataString + 'Time (sec)';
                for (let icube in this.archiveList[itrace].tray.cube)
                {
                    dataString = dataString + "," + this.archiveList[itrace].tray.cube[icube];
                }
                dataString = dataString + '\n';

                for (let ipt in  this.archiveList[itrace].trace)
                {
                    let x = (this.archiveList[itrace].trace[ipt].timeStamp - startT) / 1000;
                    dataString = dataString + x.toString();
                    for (let icube in this.archiveList[itrace].tray.cube)
                    {
                        dataString = dataString + "," + this.archiveList[itrace].trace[ipt][this.archiveList[itrace].tray.cube[icube]].toString();
                    }
                    dataString = dataString + '\n';
                }
                let data = new Blob([dataString], {type: 'text/plain'});
                return window.URL.createObjectURL(data);
            }

        }

