                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }
                this.setupPlot();
                window.addEventListener("resize", event => {this.resize()});
            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                    if (this.timePlotTraces.length > 0) this.resize();
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.timePlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            setupPlot()
            {
                this.timePlotTraces = [];
                for (let itray = 0; itray < this.config.cubeList.length; ++itray)
                {
                    for (let icube = 0; icube < this.config.cubeList[itray].length; ++icube)
                    {
                        let nameLabel = this.app.trays[itray].type + '.' + this.app.trays[itray].name + '.' + this.config.cubeList[itray][icube].name;
                        if ("label" in this.config.cubeList[itray][icube]) nameLabel = this.config.cubeList[itray][icube].label;
                        let trace = 
                        {
                            x: [],
                            y: [],
                            name:  nameLabel,
                            yaxis: this.config.cubeList[itray][icube].yaxis,
                            type: 'scatter',
                            mode: 'lines',
                            line: {color: this.config.cubeList[itray][icube].color}
                        };
                        this.timePlotTraces.push(trace);
                    }
                }
                setInterval(() => {this.resize();}, 1000);
            }
            addTracePointsToTimePlot(trayIndex)
            {
                let now = new Date().getTime();
                let plotBeginning = now - this.config.timePlotLength;
                let removeDone = false;
                if (this.timePlotTraces.length < 1) return;
                if (this.timePlotTraces[0].x.length < 1) removeDone = true;
                while(!removeDone)
                {
                    if (this.timePlotTraces[0].x[0] < plotBeginning)
                    {
                        for (let ii = 0; ii < this.timePlotTraces.length; ++ii)
                        {
                            this.timePlotTraces[ii].x.shift();
                            this.timePlotTraces[ii].y.shift();
                        }
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                let itrace = 0;
                for (let itray  = 0; itray < trayIndex; ++itray)
                {
                    itrace = itrace + this.config.cubeList[itray].length;
                }
                if (this.config.cubeList[trayIndex].length < 1) return;
                for (let icube = 0; icube < this.config.cubeList[trayIndex].length; ++icube)
                {
                    this.timePlotTraces[itrace].x.push(now);
                    this.timePlotTraces[itrace].y.push(this.app.trays[trayIndex][this.config.cubeList[trayIndex][icube].name].value);
                    itrace = itrace + 1;
                }

            }
            refresh()
            {
                let removeDone = false;
                if (this.timePlotTraces.length < 1) return;
                if (this.timePlotTraces[0].x.length < 1) removeDone = true;
                while(!removeDone)
                {
                    if (this.timePlotTraces[0].x.length > 1)
                    {
                        for (let ii = 0; ii < this.timePlotTraces.length; ++ii)
                        {
                            this.timePlotTraces[ii].x.shift();
                            this.timePlotTraces[ii].y.shift();
                        }
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
            }
        }

//gaugeCube
        class GaugeCube extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {
                this.htmlId = 'gaugePlot' + Math.floor(Math.random() * 32768).toString();

                this.gaugeDiv = document.createElement('div');
                this.gaugeDiv.setAttribute('id', this.htmlId);
                this.gaugeDiv.setAttribute('class', 'gaugeCube-div');
                widgetColumns[0].appendChild(this.gaugeDiv);
                this.gaugeContainer = widgetColumns[0];
                this.gaugeContainerRato = -1;
                let resizeObserver = new ResizeObserver((entries) => {this.updateResize(entries);});
                resizeObserver.observe(this.labelColumn);

            }
            updateResize(entries)
            {
                if (this.gaugeContainerRato < 0) return;
                this.updateCubeValue();
            }
            updateCubeValue()
            {
                if (this.gaugeContainerRato < 0)
                {
                    this.gaugeContainerRato = this.gaugeContainer.offsetWidth / this.parentElement.offsetWidth;
                }
                let gaugeMaxHeight = Math.round(this.gaugeContainerRato * (this.parentElement.offsetWidth + 40)/ 2);
                this.gaugeDiv.setAttribute('style', 'max-height:' + gaugeMaxHeight + 'px; !important;');
                this.setGaugeWidget(this.tray[this.config.cubeName]);

                return;
            }

            setGaugeWidget(cubeObj)
            {
                let degrees = 180.0 * (this.config.limits[3] - cubeObj.value) / (this.config.limits[3] - this.config.limits[0]);
                if (degrees < 0.0 )   degrees = 0.0;
                if (degrees > 180.0 ) degrees = 180.0;
                // Trig to calc meter point
                let radius = 0.50;
                let radians = degrees * Math.PI / 180;
                let x = radius * Math.cos(radians);
                let y = radius * Math.sin(radians);
                var path1 = (degrees < 45 || degrees > 135) ? 'M -0.0 -0.025 L 0.0 0.025 L ' : 'M -0.025 -0.0 L 0.025 0.0 L ';
                // Path: may have to change to create a better triangle
                let mainPath = path1,
                    pathX = String(x),
                    space = ' ',
                    pathY = String(y),
                    pathEnd = ' Z';
                let rotation = (180.0 * (this.config.limits[2] - this.config.limits[0]) / (this.config.limits[3] - this.config.limits[0])) - 90.0;
                let path = mainPath.concat(pathX,space,pathY,pathEnd);

                let valueColor = this.config.backgroundColor; 

                if ( (this.config.limits[0] <= cubeObj.value) && (cubeObj.value < this.config.limits[1]) )
                {
                    valueColor = this.config.lowColor;
                }
                if ( (this.config.limits[1]  <= cubeObj.value) && (cubeObj.value < this.config.limits[2]) )
                {
                    valueColor = this.config.mediumColor;
                }
                if ( (this.config.limits[2] <= cubeObj.value) && (cubeObj.value < this.config.limits[3]) )
                {
                  valueColor = this.config.highColor;
                }
                if ( this.config.limits[3] <= cubeObj.value)
                {
                    valueColor = this.config.highColor;
                }

                let data = 
                [
                    { 
                        type: 'scatter',
                        x: [0], y:[0],
                        marker: {size: 14, color:valueColor},
                        showlegend: false,
                        text: cubeObj.value.toString(),
                        hoverinfo: 'text',


                    },
                    { 
                        values: 
                            [
                                this.config.limits[3] - this.config.limits[2], 
                                this.config.limits[2] - this.config.limits[1], 
                                this.config.limits[1] - this.config.limits[0], 
                                this.config.limits[3] - this.config.limits[0] 
                            ],
                        rotation: rotation,
                        text: 
                            [
                                this.config.limits[2].toString() + '-' + this.config.limits[3].toString(), 
                                this.config.limits[1].toString() + '-' + this.config.limits[2].toString(), 
                                this.config.limits[0].toString() + '-' + this.config.limits[1].toString(), 
                                ''
                            ],
                        textinfo: 'text',
                        textposition:'inside',
                        textfont: {color: this.config.labelColor, size:this.config.textsize},
                        marker: 
                            {
                                colors:
                                    [
                                        this.config.highColor,
                                        this.config.mediumColor,
                                        this.config.lowColor, 
                                        this.config.backgroundColor
                                    ]
                            },
                        hoverinfo: 'label',
                        hole: 0.5,
                        type: 'pie',
                        showlegend: false,
                        sort: false
                    }
                ];
                let cubeObjValueDisp = cubeObj.value.toString() + ' ' + cubeObj.unit;
                if ( "round" in this.config)
                {
                    cubeObjValueDisp = cubeObj.value.toFixed(this.config.round) + ' ' + cubeObj.unit;
                }
//                let gaugeSize = this.config.size
                let gaugeSize = Math.round(this.gaugeContainerRato * this.parentElement.offsetWidth);
                let layout = 
                {
                    shapes:
                        [
                            {
                                type: 'path',
                                path: path,
                                fillcolor: valueColor,
                                line: {color: valueColor }
                            }
                        ],
                    height: gaugeSize,
                    width: gaugeSize,
/*
                    title: 
                        {
                            text: cubeObjValueDisp,
                            font: {color: this.config.labelColor, size:this.config.textsize},
                        },
*/
                    xaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1],
                        },
                    yaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1]
                        },
                    margin: 
                        {
                            t: 0, //top margin
                            l: 0, //left margin
                            r: 0, //right margin
                            b: 20 //bottom margin
                        },
                    plot_bgcolor:this.config.backgroundColor,
                    paper_bgcolor:this.config.backgroundColor

                };

                Plotly.newPlot(this.htmlId, data, layout, {'displayModeBar': false});
            }
        }

