                    }
                }
                setInterval(() => {this.resize();}, 1000);
            }
            addVectorsToVectorPlot(trayIndex)
            {
                let itray = 0;
                let icube = 0;
                for (let ii = 0; ii < this.vectorPlotTraces.length; ++ii)
                {
                    while (this.config.cubeList[itray].length == 0) 
                    {
                        ++itray;
                        if (itray == this.config.cubeList.length) return;
                    }
                    if (itray == trayIndex)
                    {
                        this.vectorPlotTraces[ii].x = this.app.trays[itray][this.config.cubeList[itray][icube].name].value[0];
                        this.vectorPlotTraces[ii].y = this.app.trays[itray][this.config.cubeList[itray][icube].name].value[1];
                        ++icube;
                        if (icube >= this.config.cubeList[itray].length)
                        {
                            icube = 0;
                            ++itray;
                        }
                    }
                    else
                    {
                        icube = 0;
                        ++itray;
                    }
                    if (itray >= this.config.cubeList.length) break;
                }    
            }
        }

