//dropChoiceCube
        class DropChoiceCube   extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {
                this.select = document.createElement('select');
                this.select.setAttribute('class', 'dropChoiceCube-select');
                for (let ii = 0; ii < this.config.choices.length; ++ii)
                {
                    let option = document.createElement('option');
                    option.innerHTML = this.config.choices[ii].text;
                    option.value = this.config.choices[ii].value;
                    option.style.background = 'white';
                    option.setAttribute('class', 'dropChoiceCube-select');
                    this.select.appendChild(option);
                }

                widgetColumns[0].appendChild(this.select);
                this.select.value = this.config.choices[0].value;
                this.select.style.background = this.config.choices[0].color;
                this.select.onchange = event => {this.inputChange()};

                this.unitSpan  = document.createElement('span');
                this.unitSpan.setAttribute('class', 'cubeRowParameterView-unit-span');
                this.unitSpan.innerHTML = '';
                widgetColumns[1].appendChild(this.unitSpan);

                this.settingButton = document.createElement('button');
                this.settingButton.setAttribute('class', 'btn btn-block dropChoiceSetCube-button');
                this.settingButton.innerHTML = '&#10003;';
                this.settingButton.onclick = event => {this.changeSetting()};
                widgetColumns[2].appendChild(this.settingButton);

                this.showSettingButton(false);

            }

            showSettingButton(showButton)
            {
                if (showButton)
                {
                    this.settingButton.style.display='block';
                }
                else
                {
                    this.settingButton.style.display='none';
                }
            }
            inputChange()
            {
                this.changed = true;
                this.showSettingButton(true);

                for (let ii = 0; ii < this.config.choices.length; ++ii)
                {
                    if (this.select.value == this.config.choices[ii].value)
                    {
                        this.select.style.background = this.config.choices[ii].color;
                    }
                }
            }
            changeSetting()
            {

                this.showSettingButton(false);
                let payload = {'cube': this.config.cubeName, 'value':Number(this.select.value)};
                let actionMsg = 
                    {
                        topic   : this.card.app.box + '/tray/' +  this.tray.type + '/' +  this.tray.name + '/setting/setting',
                        payload : payload
                    };
                this.card.app.sendActionMsg('setting','setting',actionMsg);
                this.changed = false;
            }
            updateCubeValue()
            {
                if (this.tray[this.config.cubeName].hasOwnProperty('enabled') )
                {
                    if (this.tray[this.config.cubeName].enabled == 1)
                    {
                        this.select.disabled = false;
                    }
                    else
                    {
                        this.select.disabled = true;
                    }
                }

                if (!this.changed) 
                {
                    this.select.value = this.tray[this.config.cubeName].value;
                    this.unitSpan.innerHTML    = this.tray[this.config.cubeName].unit;
                    for (let ii = 0; ii < this.config.choices.length; ++ii)
                    {
                        if (this.select.value == this.config.choices[ii].value)
                        {
                            this.select.style.background = this.config.choices[ii].color;
                        }
                    }
                }

            }
        }

