var boxCookie = msg.req.cookies[global.get('box') + 'Role'];
if(boxCookie == undefined)
{
    msg.cookies = { };
    msg.payload.errorMsg = "Credentials needed";
    return [null,msg];
}
msg.token = boxCookie;
msg.payload['roleToken'] = msg.token;
return [msg,null];

var tokenError = msg.error;
if (tokenError != undefined)
{
    msg.payload.errorMsg = tokenError;
    msg.cookies = { };
    return [null,msg];
}
if (msg.token.expDate > 0)
{
    if (msg.token.expDate < new Date().getTime())
    {
        msg.payload.errorMsg = 'Login Expired. Please login again';
        msg.cookies = { };
        return [null,msg];
    }
}
let permittedRole = false;

for (let irole = 0; irole < msg.payload.allowedRoles.length; ++irole)
{
    for (let itoken = 0; itoken < msg.token.roles.length; ++itoken)
    {
        if (msg.token.roles[itoken] == msg.payload.allowedRoles[irole]) permittedRole = true;
    }
}
if (!permittedRole) return credsNotValid(msg, 'Credentials not valid for App');

let trayList = JSON.parse(msg.payload.urlInput).trayList;
let trayOk = [];
for (let il = 0; il < trayList.length; ++il)
{
    let trayPermitted = false;
    for (let itray = 0; itray < msg.token.trays.length; ++itray)
    {
        if (msg.token.trays[itray].type == '*')
        {
            trayPermitted = true;
            break;
        }
        if (msg.token.trays[itray].type == trayList[il].type)
        {
            if (msg.token.trays[itray].name == '*')
            {
                trayPermitted = true;
                break;
            }
            if (msg.token.trays[itray].name == trayList[il].name)
            {
                trayPermitted = true;
                break;
            }
        }
    }
    trayOk.push(trayPermitted);
}
let trayPermitted = true;
for (let il = 0; il < trayOk.length; ++il)
{
    if (!trayOk[il]) trayPermitted = false;
}
if (!trayPermitted) return credsNotValid(msg, 'Credentials not valid for data');

msg.payload['profile'] = JSON.stringify(msg.token);
return [msg,null];

function credsNotValid(messgage, notification)
{
    messgage.payload.errorMsg = notification;
    messgage.cookies = { };
    return [null,messgage];
}

