trayUpdate(tray, trayIndex)
{
    if (trayIndex != 0) return;
    this.tray = tray;
    if (this.tray.triggerMode.value == 0)
    {
        this.manualTriggerButton.style.display = "block";
    }
    else
    {
        this.manualTriggerButton.style.display = "none";
    }
    if (this.tray.manualTrigger.value == 0)
    {
        this.manualTriggerButton.innerText = "Take Image";
    }
    else
    {
        this.manualTriggerButton.innerText = "Abort";
    }

    if (this.tray.imageData.value != null)
    {
        this.timestampSpan.innerText = new Date(this.tray.timeStamp).toLocaleString("en-SE", { timeZone: "Europe/Stockholm" });
        this.sensorNameSpan.innerText = this.tray.alias.value;
        this.pgm = JSON.parse(JSON.stringify(this.tray.imageData.value));
        this.imageArray = this.pgm.imageArray;
        if (this.tray.transpose.value == 0)
        {
            this.flirCanvas.width = "800";
            this.flirCanvas.height = "600";
        }
        else
        {
            this.flirCanvas.width = "800";
            this.flirCanvas.height = "1067";
        }
        this.plotImage();
    }
}
plotImage()
{
    let rgb = [];
    let plow = (this.pgm.minval - 27315) / 100;
    let phigh = (this.pgm.maxval - 27315) / 100;
    let pdelta = phigh - plow;
    for (let ibox = 0; ibox < 9; ++ibox)
    {
        let intensity = ibox / 8.0;
        let pbox = Math.round(((phigh - plow) * intensity + plow) * 10.0) / 10.0;
        this.colorScaleBoxSpan[ibox].innerText = pbox.toString();
        if (this.enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
        if (this.enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
        if (this.enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
        if (this.enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
        if (this.enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
        if (this.colorScale)
        {
            rgb = this.getRGB(intensity);
        }
        else
        {
            rgb[0] = Math.floor(255 * intensity);
            rgb[1] = Math.floor(255 * intensity);
            rgb[2] = Math.floor(255 * intensity);
        }
        this.colorScaleBoxDiv[ibox].style.backgroundColor = "rgb(" + rgb[0].toString() + ", " + rgb[1].toString() + ", " + rgb[2].toString() + ")";
    }
    let canvasContext = this.flirCanvas.getContext("2d");
    let tipPointerContext = this.tipPointerCanvas.getContext("2d");
    let tipContext = this.tipCanvas.getContext("2d");
    tipContext.font = "30px Arial";
    tipContext.fillStyle = "cyan";
    tipPointerContext.fillStyle = "cyan";
    if (this.colorScale) tipContext.fillStyle = "black";
    if (this.colorScale) tipPointerContext.fillStyle = "black";
    tipContext.textAlign = "left";

    let canvasWidth  = Number(this.flirCanvas.width);
    let wrapperWidth = Number(this.wrapperDiv.width);
    let xOffsetKluge = Math.round((wrapperWidth - canvasWidth) / 2);
    let canvasHeight = Number(this.flirCanvas.height);
    let rectWidth  = Math.round(canvasWidth  / this.pgm.ncols);
    let rectHeight = Math.round(canvasHeight / this.pgm.nrows);

    for (let irow = 0; irow < this.pgm.nrows; ++irow)
    {
        for (let icol = 0; icol < this.pgm.ncols; ++icol)
        {
            let intensity = this.imageArray[irow][icol] / 256.0;
            if (this.enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
            if (this.enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
            if (this.enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
            if (this.enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
            if (this.enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
            if (this.colorScale)
            {
                rgb = this.getRGB(intensity);
                rgb[0] = Math.floor(rgb[0]);
                rgb[1] = Math.floor(rgb[1]);
                rgb[2] = Math.floor(rgb[2]);
            }
            else
            {
                rgb[0] = Math.floor(255 * intensity);
                rgb[1] = Math.floor(255 * intensity);
                rgb[2] = Math.floor(255 * intensity);
            }
            canvasContext.fillStyle = "rgb(" + rgb[0].toString() + ", " + rgb[1].toString() + ", " + rgb[2].toString() + ")";
            canvasContext.fillRect(icol * rectWidth, canvasHeight - rectHeight - irow * rectHeight, rectWidth, rectHeight);
        }    
    }
    this.imageLink.download = this.tray.type + "-" + this.tray.name + ".png";
    this.imageLink.href = this.flirCanvas.toDataURL('image/png');
}
onMouseMove(e)
{
    let tipPointerContext = this.tipPointerCanvas.getContext("2d");
    let tipContext = this.tipCanvas.getContext("2d");
    let canvasWidth  = Number(this.flirCanvas.width);
    let wrapperWidth = Number(this.wrapperDiv.width);
    let xOffsetKluge = Math.round((wrapperWidth - canvasWidth) / 2);
    let canvasHeight = Number(this.flirCanvas.height);
    let rectWidth  = Math.round(canvasWidth  / this.pgm.ncols);
    let rectHeight = Math.round(canvasHeight / this.pgm.nrows);

    let plow = (this.pgm.minval - 27315) / 100;
    let phigh = (this.pgm.maxval - 27315) / 100;
    let pdelta = phigh - plow;

    let icol = Math.floor(e.layerX / rectWidth);
    if (icol < 0) icol = 0;
    if (icol >= this.pgm.ncols) icol = this.pgm.ncols - 1;
    let irow = Math.floor((canvasHeight - e.layerY) / rectHeight);
    if (irow < 0) irow = 0;
    if (irow >= this.pgm.nrows) irow = this.pgm.nrows - 1;
    let temp = Math.floor(100.0 * (pdelta * this.imageArray[irow][icol] / 256.0 + plow)) / 100;

    this.tipPointerCanvas.style.top = (e.layerY  - this.tipPointerCanvas.height / 2).toString()  + "px";
    this.tipPointerCanvas.style.left = (xOffsetKluge + e.layerX - this.tipPointerCanvas.width / 2).toString() + "px";

    tipPointerContext.clearRect(0, 0, this.tipPointerCanvas.width, this.tipPointerCanvas.height);
    tipPointerContext.fillRect( 0, 0, this.tipPointerCanvas.width, this.tipPointerCanvas.height);

    this.tipCanvas.style.top = (e.layerY - this.tipCanvas.height - this.tipPointerCanvas.height).toString()  + "px";
    this.tipCanvas.style.left = (xOffsetKluge + e.layerX).toString() + "px";
    tipContext.clearRect(0, 0, this.tipCanvas.width, this.tipCanvas.height);

    let coord = this.tipPointerCanvas.style.top + "," + this.tipPointerCanvas.style.left;
    tipContext.fillText(temp.toString() + " C", 0, 30);

}
mouseLeftCanvas()
{
    let tipContext = this.tipCanvas.getContext("2d");
    tipContext.clearRect(0, 0, this.tipCanvas.width, this.tipCanvas.height);
    let tipPointerContext = this.tipPointerCanvas.getContext("2d");
    tipPointerContext.clearRect(0, 0, this.tipPointerCanvas.width, this.tipPointerCanvas.height);
}
getRGB(intensity)
{
    let rgb = [0,0,0];
    let rgbCoeff = [
        [  48.6, 349.0,    5.72, -172.0],
        [-164.0, 705.0, -667.0 ,  379.0],
        [ 135.0, 332.0, -976.0,   536.0]];
    let intensityStart = [0.0, 0.3176, 0.0];
    let power = [1.0, 0.0, 0.0, 0.0];
    for (let ii = 1; ii < 4; ++ii) power[ii] = power[ii - 1] * intensity;
    for (let irgb = 0; irgb < 3; ++irgb)
    {
        if (intensity >= intensityStart[irgb])
        {
            for (let ipow = 0; ipow < 4; ++ipow)
            {
                rgb[irgb] = rgb[irgb] + rgbCoeff[irgb][ipow] * power[ipow];
            }
        }
    }
    return rgb;
}
getRGBRainbow(intensity)
{
    let rgb = [0,0,0];
    if ( (0 <= intensity) && (intensity <= 0.25) )
    {
        rgb[0] = 0;
        rgb[1] = Math.floor(255 * intensity * 4);    
        rgb[2] = 255;
    }
    if ( (0.25 < intensity) && (intensity <= 0.50) )
    {
        rgb[0] = 0;
        rgb[1] = 255;    
        rgb[2] = Math.floor(255 * (0.50 - intensity) * 4);
    }
    if ( (0.50 < intensity) && (intensity <= 0.75) )
    {
        rgb[0] = Math.floor(255 * (intensity - 0.50) * 4);
        rgb[1] = 255;    
        rgb[2] = 0;
    }
    if ( (0.75 < intensity) && (intensity <= 1.0) )
    {
        rgb[0] = 255;
        rgb[1] = Math.floor(255 * (1.0 - intensity) * 4);    
        rgb[2] = 0;
    }
    return rgb;
}
toggleColorScale()
{
    this.colorScale = !this.colorScale;
    if (this.colorScale)
    {
        this.colorScaleButton.innerText = "Color";
    }
    else
    {
        this.colorScaleButton.innerText = "Gray";
    }
    this.plotImage();
}
toggleEnhancedScale(deltaEnhanced)
{
    this.enhancementLevel = this.enhancementLevel + deltaEnhanced;
    if (this.enhancementLevel >  2) this.enhancementLevel = 2;
    if (this.enhancementLevel < -2) this.enhancementLevel = -2;
    this.enhancementLevelSpan.innerText = this.enhancementLevel.toString();
    this.plotImage();
}
clearImage()
{
    let canvasContext = this.flirCanvas.getContext("2d");
    let canvasWidth  = Number(this.flirCanvas.width);
    let canvasHeight = Number(this.flirCanvas.height);
    canvasContext.clearRect(0, 0, canvasWidth, canvasHeight); 
    this.timestampSpan.innerText = "-";       
    for (let ibox = 0; ibox < 9; ++ibox)
    {
        let intensity = ibox / 8.0;
        let pbox = 0.0;
        this.colorScaleBoxSpan[ibox].innerText = pbox.toString();
     } 
}
manualTrigger()
{
    let setting = 1;
    if (this.tray.manualTrigger.value == 1)
    {
        setting = 0;
    }
    let payload = {"cube": "manualTrigger", "value":setting};
    let actionMsg = 
        {
            topic   : this.app.box + "/tray/" +  this.tray.type + "/" +  this.tray.name + "/setting/setting",
            payload : payload
        };
    this.app.sendActionMsg("setting","setting",actionMsg);
    this.tray.manualTrigger.value = setting;

    if (this.tray.manualTrigger.value == 0)
    {
        this.manualTriggerButton.innerText = "Take Image";
    }
    else
    {
        this.manualTriggerButton.innerText = "Abort";
    }

}
handleArchiveCardClick(clickData)
{
    let projectionFilter =  
    {
        projection:
        {
            _id                         :   0, 
        } 
    };
    let queryFilter = 
    {
        $and :
        [
            {
                type: this.tray.type
            },
            {
                name: this.tray.name
            },
            {
                timeStamp :
                {
                    $gte: clickData[0].timeStamp - 1,
                    $lte: clickData[0].timeStamp + 1
                }
            }
        ] 

    }
    this.app.sendActionMsg("customReadArchive","readDatabase",{queryFilter:queryFilter, projectionFilter:projectionFilter});
}
