        class SettingIncrCube   extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {
                this.setting = 0;
                this.newSetting = 0;
                let incrTable = document.createElement("table");
                incrTable.width = "100%"
                let incrTableRow = document.createElement("tr");
                incrTable.appendChild(incrTableRow)

                let incTableRowCol1 = document.createElement("td");
                incrTableRow.appendChild(incTableRowCol1);
                incTableRowCol1.width = "20%";
                incTableRowCol1.align = "center";

                this.minusButton = document.createElement("button");
                incTableRowCol1.appendChild(this.minusButton);
                this.minusButton.classList.add("btn");
                this.minusButton.classList.add("btn-block");
                this.minusButton.classList.add("dropChoiceSetCube-button");
                this.minusButton.innerHTML = "-";
                this.minusButton.onclick = event => {this.increment(-this.config.increment)};

                let incTableRowCol2 = document.createElement("td");
                incrTableRow.appendChild(incTableRowCol2);
                incTableRowCol2.width = "60%";
                incTableRowCol2.align = "center";

                this.readingSpan  = document.createElement("span");
                incTableRowCol2.appendChild(this.readingSpan);
                this.readingSpan.setAttribute("class", "numberReadCube-span");
                this.readingSpan.innerHTML = "?";

                let incTableRowCol3 = document.createElement("td");
                incrTableRow.appendChild(incTableRowCol3);
                incTableRowCol3.width = "20%";
                incTableRowCol3.align = "center";

                this.plusButton = document.createElement("button");
                incTableRowCol3.appendChild(this.plusButton);
                this.plusButton.classList.add("btn");
                this.plusButton.classList.add("btn-block");
                this.plusButton.classList.add("dropChoiceSetCube-button");
                this.plusButton.innerHTML = "+";
                this.plusButton.onclick = event => {this.increment(this.config.increment)};

                widgetColumns[0].appendChild(incrTable);

                this.unitSpan  = document.createElement("span");
                this.unitSpan.setAttribute("class", "cubeRowParameterView-unit-span");
                this.unitSpan.innerHTML = "";
                widgetColumns[1].appendChild(this.unitSpan);


            }
            increment(deltaInc)
            {
                this.newSetting = Math.round(this.setting + deltaInc);
                this.changeSetting();
            }
            changeSetting()
            {
                let lolo = this.tray[this.config.cubeName].alarm.limits.lolo;
                let hihi = this.tray[this.config.cubeName].alarm.limits.hihi;
                if ((this.newSetting <= lolo) || (this.newSetting >= hihi))
                {
                    this.newSetting = this.setting;
                    this.card.app.acknowledgeDialog.showDialog('Error: Setting outside range',() => {});
                }
                let payload = {"cube": this.config.cubeName, "value":this.newSetting};
                let actionMsg = 
                    {
                        topic   : this.card.app.box + "/tray/" +  this.tray.type + "/" +  this.tray.name + "/setting/setting",
                        payload : payload
                    };
                this.card.app.sendActionMsg("setting","setting",actionMsg);
            }
            updateCubeValue()
            {
                if (this.tray[this.config.cubeName].hasOwnProperty('enabled') )
                {
                    if (this.tray[this.config.cubeName].enabled == 1)
                    {
                        this.minusButton.disabled = false;
                        this.plusButton.disabled = false;
                    }
                    else
                    {
                        this.minusButton.disabled = true;
                        this.plusButton.disabled = true;
                    }
                }
                this.setting = Number(this.tray[this.config.cubeName].value);
                this.newSetting = this.setting;
                this.readingSpan.innerHTML = this.tray[this.config.cubeName].value.toString();
                this.unitSpan.innerHTML    = this.tray[this.config.cubeName].unit;
            }
        }

