            {
                this.card.app.alarmLimitDialog.editAlarmConfig(this.config.cubeName, this.tray);
            }
            setHorzBarWidget(cubeObj)
            {
                let lowValue = 0;
                let mediumValue = 0;
                let highValue = 0;

                let lowColor = this.config.backgroundColor;
                let mediumColor = this.config.backgroundColor;
                let highColor = this.config.backgroundColor;
                let valueColor = this.config.backgroundColor; 

                if ( cubeObj.value <= this.config.limits[0])
                {
                    lowValue = this.config.limits[0];
                    lowColor = this.config.lowColor;
                    valueColor = lowColor;
                }
                if ( (this.config.limits[0] <= cubeObj.value) && (cubeObj.value < this.config.limits[1]) )
                {
                    lowValue = cubeObj.value;
                    lowColor = this.config.lowColor;
                    valueColor = lowColor;
                }
                if ( (this.config.limits[1]  <= cubeObj.value) && (cubeObj.value < this.config.limits[2]) )
                {
                    lowValue = this.config.limits[1];
                    mediumValue = cubeObj.value - this.config.limits[1]; 
                    lowColor = this.config.mediumColor;
                    mediumColor = this.config.mediumColor;
                    valueColor = mediumColor;

                }
                if ( (this.config.limits[2] <= cubeObj.value) && (cubeObj.value < this.config.limits[3] ) )
                {
                    lowValue = this.config.limits[1];
                    mediumValue = this.config.limits[2] - this.config.limits[1];
                    highValue = cubeObj.value - this.config.limits[2];
                    lowColor = this.config.highColor;
                    mediumColor = this.config.highColor;
                    highColor = this.config.highColor;
                    valueColor = highColor;
                }
                if ( this.config.limits[3] <= cubeObj.value)
                {
                    lowValue = this.config.limits[1];
                    mediumValue = this.config.limits[2] - this.config.limits[1]; 
                    highValue = this.config.limits[3]- this.config.limits[2]; 
                    lowColor = this.config.highColor;
                    mediumColor = this.config.highColor;
                    highColor = this.config.highColor;
                    valueColor = highColor;
                }

                let lowBar = 
                {
                    y: ['', '-'],
                    x: [ this.config.limits[1], lowValue],
                    type: 'bar',
                    orientation: 'h',
                    showlegend: false,
                    marker:{color: [this.config.lowColor, lowColor]},
                    width: [0.1, 1.0],
                };
                let mediumBar = 
                {
                    y: ['', '-'],
                    x: [this.config.limits[2] - this.config.limits[1], mediumValue],
                    type: 'bar',
                    orientation: 'h',
                    showlegend: false,
                    marker:{color: [this.config.mediumColor, mediumColor]},
                    width: [0.1, 1.0],
                };
                let highBar = 
                {
                    y: ['', '-'],
                    x: [this.config.limits[3] - this.config.limits[2], highValue],
                    orientation: 'h',
                    type: 'bar',
                    showlegend: false,
                    marker:{color: [this.config.highColor, highColor]},
                    width: [0.1, 1.0],
                };
                let cubeObjValueDisp = cubeObj.value.toString() + ' ' + cubeObj.unit;
                if ( "round" in this.config)
                {
                    cubeObjValueDisp = cubeObj.value.toFixed(this.config.round) + ' ' + cubeObj.unit;
                }
                let layout = 
                {
                    barmode: 'relative',
                    plot_bgcolor:this.config.backgroundColor,
                    paper_bgcolor:this.config.backgroundColor,
                    margin: 
                    {
                        t: 70, //top margin
                        l: 20, //left margin
                        r: 20, //right margin
                        b: 70 //bottom margin
                    },

                    height: this.config.height,
                    title: 
                    {
                        text: cubeObjValueDisp,
                        font: {color: this.config.labelColor, size:this.config.textsize},
                    },
                    yaxis: 
                    {
                        tickfont: {color:valueColor, size:this.config.textsize}
                    },
                    xaxis: 
                    {
                        range: [this.config.limits[0], this.config.limits[3]],
                        tickfont: {color:this.config.labelColor, size:this.config.textsize},
                        gridcolor       : this.config.gridColor

                    },
                };
               Plotly.newPlot(this.htmlId, [lowBar, mediumBar, highBar], layout, {'displayModeBar': false});
            }
        }

