                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }
                this.setupPlot();
                window.addEventListener("resize", event => {this.resize()});
            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                    if (this.barPlotTraces.length > 0) this.resize();
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.barPlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot(this.plotlyId, this.barPlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            setupPlot()
            {
                this.barPlotTraces = [];
                let trace = 
                {
                    x: [],
                    y: [],
                    type: 'bar',
                    marker:
                    {
                        color: []
                    },
                    orientation: 'v',
                };
                this.barPlotTraces.push(trace);
                trace.orientation = this.config.orientation;
                for (let icube = 0; icube < this.config.cubeList.length; ++icube)
                {
                    let tray = this.app.trays[this.config.cubeList[icube].trayIndex];
                    let nameLabel = tray.type + '.' + tray.name + '.' + this.config.cubeList[icube].cubeName;
                    if ("cubeLabel" in this.config.cubeList[icube]) nameLabel = this.config.cubeList[icube].cubeLabel;
                    if (trace.orientation === 'v')
                    {
                        trace.x.push(nameLabel);
                        trace.y.push(0.0000000001);
                    }
                    else
                    {
                        trace.x.push(0.0000000001);
                        trace.y.push(nameLabel);
                    }
                    if ('color' in this.config.cubeList[icube])
                    {
                        trace.marker.color.push(this.config.cubeList[icube].color);
                    }
                    else
                    {
                        trace.marker.color.push('#1f77b4'); 
                    }
                }
                setInterval(() => {this.resize();}, 1000);
            }
            addTracePointsToBarPlot(trayIndex)
            {
                if (this.barPlotTraces.length < 1) return;
                let trace = this.barPlotTraces[0];
                let tray = this.app.trays[trayIndex];
                for (let icube = 0; icube < this.config.cubeList.length; ++icube)
                {
                    if (this.config.cubeList[icube].trayIndex == trayIndex) 
                    {
                        if (trace.orientation == 'v')
                        {
                            trace.y[icube] = tray[this.config.cubeList[icube].cubeName].value;
                        }
                        else
                        {
                            trace.x[icube] = tray[this.config.cubeList[icube].cubeName].value;
                        }
                    }
                }

            }
        }

