//navbar
        class NavBar 
        {
            constructor(app, config)
            {
                this.app = app;
                this.config = config;
            }
            onDocumentReady()
            {
                let navbarDiv = document.createElement('div');
                navbarDiv.setAttribute('class', 'jumbotron navbar');
                navbarDiv.setAttribute('width', '100%');
                document.getElementById('appContent').appendChild(navbarDiv);

                let navbarTable = document.createElement('table');
                navbarTable.setAttribute('width', '100%');
                navbarDiv.appendChild(navbarTable);
                let row = document.createElement('tr');
                navbarTable.appendChild(row);

                let col1 = document.createElement('td');
                col1.setAttribute('width', '20%');
                col1.setAttribute('style', 'text-align:left; vertical-align:middle;');
                row.appendChild(col1);

                let col1Link = document.createElement('a');
                col1Link.setAttribute('href', '/');
                col1.appendChild(col1Link);

                let col1Image = document.createElement('img');
                col1Image.setAttribute('src', '{{{generalAppConfig.projectLogo}}}');
                col1Image.setAttribute('height', this.config.imageHeight);
                col1Link.appendChild(col1Image);

                let col2 = document.createElement('td');
                col2.setAttribute('width', '60%');
                col2.setAttribute('style', 'text-align:center; vertical-align:middle;');
                row.appendChild(col2);

                let col2Header = document.createElement('h1');
                col2Header.setAttribute('class', 'navbar-title');
                col2.appendChild(col2Header);

                let col2Image = document.createElement('img');
                col2Image.setAttribute('src', this.config.centerImage);
                col2Image.setAttribute('height', this.config.imageHeight);
                col2Image.setAttribute('style', "padding-right:20px;");
                col2Header.appendChild(col2Image);

                let col2Span = document.createElement('span');
                col2Span.innerHTML = this.app.config.title;
                col2Header.appendChild(col2Span);

