            showAlarmConfig(show)
            {
                if (show)
                {
                    this.cardContainer.style.display = "block";
                    this.limitCard.style.display = "block";
                }
                else
                {
                    this.cardContainer.style.display = "none";
                }
            }
            cancel()
            {
                this.cardContainer.style.display = "none";
            }
            changeLimits()
            {
                for (let ii = 0; ii < 4; ++ii) 
                {
                    if (isNaN(this.limitInput[ii].value) )
                    {
                        this.cardContainer.style.display = "none";
                        return;
                    }
                }
                this.tray[this.cubeName].alarm.limits.lolo = Number(this.limitInput[0].value);
                this.tray[this.cubeName].alarm.limits.low  = Number(this.limitInput[1].value);
                this.tray[this.cubeName].alarm.limits.high = Number(this.limitInput[2].value);
                this.tray[this.cubeName].alarm.limits.hihi = Number(this.limitInput[3].value);
                let configInfo = 
                {
                    cube : this.cubeName,
                    limits: this.tray[this.cubeName].alarm.limits,
                };
                if (this.tray[this.cubeName].hasOwnProperty('enabled') )
                {
                    configInfo['enabled'] = Number(this.settingEnableButton.value);
                }

                this.updateTrayConfig(configInfo);
                this.cardContainer.style.display = "none";
            }
            editAlarmConfig(cubeName, tray)
            {
                this.cubeName = cubeName;
                this.tray = tray;
                this.cardTitle.innerHTML = this.tray.type + "." + this.tray.name + "." + this.cubeName;
                this.limitInput[0].value = this.tray[this.cubeName].alarm.limits.lolo.toString();
                this.limitInput[1].value = this.tray[this.cubeName].alarm.limits.low.toString();
                this.limitInput[2].value = this.tray[this.cubeName].alarm.limits.high.toString();
                this.limitInput[3].value = this.tray[this.cubeName].alarm.limits.hihi.toString();
                if (this.tray[this.cubeName].hasOwnProperty('enabled') )
                {
                    this.settingEnableTr.style.position = 'static';
                    if (this.tray[this.cubeName].enabled == 1)
                    {
                        this.settingEnableButton.value = '1';
                        this.settingEnableButton.style.backgroundColor = "green";
                        this.settingEnableButton.innerHTML = 'Enabled';
                    }
                    else
                    {
                        this.settingEnableButton.value = '0';
                        this.settingEnableButton.style.backgroundColor = "red";
                        this.settingEnableButton.innerHTML = 'Disabled';
                    }
                }
                else
                {
                    this.settingEnableTr.style.position = 'absolute';
                    this.settingEnableTr.style.right = '100%';
                }
                this.showAlarmConfig(true);

            }
            updateTrayConfig(configInfo)
            {
                let actionMsg = 
                {
                    topic   : this.app.box + '/tray/' +  this.tray.type + '/' +  this.tray.name + '/setting/config',
                    payload : configInfo
                };
                this.app.sendActionMsg('config','setting',actionMsg);
            }
            createSettingEnableTr()
            {
                let limitSpan  = document.createElement('span');
                limitSpan.setAttribute('class', 'alarmLimit-label-text');
                limitSpan.innerHTML = "Setting";

                let limitSpanTd = document.createElement('td');
