                this.settingEnableButton = document.createElement('button');
                this.settingEnableButton.style.fontSize = "300%";
                this.settingEnableButton.style.color = "white";
                this.settingEnableButton.style.backgroundColor = "white";
                this.settingEnableButton.style.width = "100%";
                this.settingEnableButton.innerHTML = 'Enabled';
                this.settingEnableButton.value = '-1';
                this.settingEnableButton.onclick = event => {this.changeSettingEnableButton()};

                let settingEnableTd = document.createElement('td');
                settingEnableTd.setAttribute('width', '50%');
                settingEnableTd.setAttribute('align', 'right');
                settingEnableTd.appendChild(this.settingEnableButton);

                let settingEnableTr = document.createElement('tr');
                settingEnableTr.appendChild(limitSpanTd);
                settingEnableTr.appendChild(settingEnableTd);

                return settingEnableTr;

            }
            changeSettingEnableButton()
            {
                if (this.settingEnableButton.value == '0')
                {
                    this.settingEnableButton.value = '1';
                    this.settingEnableButton.style.backgroundColor = "green";
                    this.settingEnableButton.innerHTML = 'Enabled';
                }
                else
                {
                    this.settingEnableButton.value = '0';
                    this.settingEnableButton.style.backgroundColor = "red";
                    this.settingEnableButton.innerHTML = 'Disabled';
                }
            }
        }

