                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            getCreds()
            {
                let now = new Date().getTime();
                let expDate = Number(this.app.getCookie('ExpDate'));
                if (expDate > 0)
                {
                    expDate = expDate - now;
                    if(expDate < 0)
                    {
                        logout();
                    }
                    else
                    {
                        let hours = Math.floor(expDate / 3600000);
                        let minutes = expDate - hours * 3600000;
                        minutes = Math.floor(minutes / 60000);
                        let seconds = expDate - (hours * 3600000) - (minutes * 60000);
                        seconds = Math.floor(seconds / 1000);
                        hours = hours.toString();
                        if (hours.length == 1) hours = '0' + hours; 
                        minutes = minutes.toString();
                        if (minutes.length == 1) minutes = '0' + minutes; 
                        seconds = seconds.toString();
                        if (seconds.length == 1) seconds = '0' + seconds; 
                        let timeString = hours + ':' + minutes + ':' + seconds;
                        this.userNameSpan.innerHTML = this.app.getCookie('Username');
                        this.expTimeSpan.innerHTML = timeString;
                    }
                }
                else
                {
                    this.userNameSpan.innerHTML = this.app.getCookie('Username');
                    this.expTimeSpan.innerHTML = 'Forever';
                }
            }
            logout()
            {
                document.cookie = this.app.box + "Role= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
                document.cookie = this.app.box + "ExpDate= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
                document.cookie = this.app.box + "Username= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
                window.location.href = "/";
            }
            renew()
            {
                let actionMsg = {};
                this.app.sendActionMsg('renew', 'renew', actionMsg);
            }
            downloadToken()
            {
                downloadString(this.getCookie("Role"), "text/plain", box + "-token.txt");
            }
    // from https://gist.github.com/danallison/3ec9d5314788b337b682
            downloadString(text, fileType, fileName) 
            {
                let blob = new Blob([text], { type: fileType });

                let a = document.createElement('a');
                a.download = fileName;
                a.href = URL.createObjectURL(blob);
                a.dataset.downloadurl = [fileType, a.download, a.href].join(':');
                a.style.display = "none";
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                setTimeout(function() { URL.revokeObjectURL(a.href); }, 1500);
            }    
        }

