if (msg.token.timeoutMin > 0) msg.token.expDate = new Date().getTime() + msg.token.timeoutMin * 60000;
delete msg.token['iat']
msg.payload = JSON.parse(JSON.stringify(msg.token));
delete msg['token'];
return msg;

var newMsg = 
{
    topic : 'renew',
    payload: 
    {
        topic       : 'renew',
        userID      : msg.userID,
        role        : msg.token,
        expDate     : msg.payload.expDate.toString(),
        username    : msg.payload.username
    }
}
return newMsg;

var projectionFilter =  
{
    projection:
    {
        _id : 0, 
    } 
};
var queryFilter = 
{
    timeStamp :
    {
        $gte: msg.payload.startDate,
        $lte: msg.payload.stopDate
    }
}
var newMsg = 
{
    topic           : 'getSettingsLog',
    userID          : msg.userID,
    payload         : [queryFilter, projectionFilter],
    trayList        : msg.payload.trayList
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? 1 : ((x > y) ? -1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic : 'getSettingsLog',
        payload:{
            topic           : 'getSettingsLog',
            payload         : null,
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) 
{
    let splitTopic = msg.payload[ii].topic.split('/');
    let type = splitTopic[2];
    let name = splitTopic[3];
    let allowedTray = false;
    for (let itray in msg.trayList)
    {
        if ((msg.trayList[itray].type == type) && (msg.trayList[itray].name == name) )
        {
            allowedTray = true;
            break;
        }
    }
    if (allowedTray)
    {
        let data = 
        {
            type        : type,
            name        : name,
            cube        : msg.payload[ii].tray.cube,
            value       : msg.payload[ii].tray.value,
            timeStamp   : msg.payload[ii].timeStamp,
            username    : msg.payload[ii].username,
            url         : msg.payload[ii].ipInfo.url
        }
        records.push(data); 
    }
}
sortByKey(records, 'timeStamp');

return {
    payload:{
        topic           : 'getSettingsLog',
        payload         : records,
        userID          : msg.userID,
    }
};

msg.userData = {cred : {}, url : msg.payload.url};
msg.userData.cred = 
{
    username : msg.payload.username, 
    optCode : msg.payload.optCode,
};
msg.payload =  [{username : msg.userData.cred.username },  {projection  :{_id :   0}}]
return msg;

function isEmpty(obj) {
    for(var key in obj) {
        if(obj.hasOwnProperty(key))
            return false;
    }
    return true;
}
if (isEmpty(msg.payload))
{
    delete msg.userData['cred'];
    msg.payload.errorMsg = 'Error: Invalid Credentials';
    return [msg, null,null,null];
}
let { authenticator } = global.get('otplib');
if (("secret2Fa" in msg.payload))
{
    if (!authenticator.check(msg.userData.cred.optCode, msg.payload.secret2Fa))
    {
        delete msg.userData['cred'];
        msg.payload = {errorMsg: 'Error: Invalid Credentials'};
        return [msg, null,null,null];
    }
    else
    {
        msg.payload = JSON.parse(JSON.stringify(msg.payload.profile));
        msg.payload['username'] = msg.userData.cred.username;
        msg.payload['expDate'] = -1;
        if ( msg.payload.timeoutMin > 0)
        {
            msg.payload['expDate'] = new Date().getTime() + msg.payload.timeoutMin * 60000;
        }
        delete msg.userData.cred;
        return [null,msg,null,null];
    }
}
if (("tempSecret2Fa" in msg.payload))
{
    if (!authenticator.check(msg.userData.cred.optCode, msg.payload.tempSecret2Fa))
    {
        delete msg.userData['cred'];
        msg.payload = {errorMsg: 'Error: Invalid Credentials'};
        return [msg, null,null,null];
    }
    else
    {
        let queryFilter = 
        {
            username : msg.userData.cred.username
        }
        let updateSecretMsg = {topic:'updateSecret', payload:[queryFilter, { $set: {secret2Fa:msg.payload.tempSecret2Fa} }]};
        msg.payload = JSON.parse(JSON.stringify(msg.payload.profile));
        msg.payload['username'] = msg.userData.cred.username;
        msg.payload['expDate'] = -1;
        if ( msg.payload.timeoutMin > 0)
        {
            msg.payload['expDate'] = new Date().getTime() + msg.payload.timeoutMin * 60000;
        }
        delete msg.userData.cred;

        return [null,msg,updateSecretMsg,null];
    }
}
msg.payload = {username: msg.userData.cred.username};
delete msg.userData['cred'];
return [null,null,null,msg];

