let tray = msg.payload;
let alarmList = flow.get('alarmList');
let keys = Object.keys(tray);
let newAlarmList = [];
for (let ikey in keys)
{
    if (tray[keys[ikey]].hasOwnProperty('type'))
    {
        if (tray[keys[ikey]].type == 'scalar')
        {
            if (tray[keys[ikey]].hasOwnProperty('alarm'))
            {
                if (!isNaN(tray[keys[ikey]].value))
                {
                    tray[keys[ikey]].alarm.value = 0;
                    if (tray[keys[ikey]].value <= tray[keys[ikey]].alarm.limits.low)  tray[keys[ikey]].alarm.value = 1;
                    if (tray[keys[ikey]].value <= tray[keys[ikey]].alarm.limits.lolo) tray[keys[ikey]].alarm.value = 3;
                    if (tray[keys[ikey]].value >= tray[keys[ikey]].alarm.limits.high) tray[keys[ikey]].alarm.value = 2;
                    if (tray[keys[ikey]].value >= tray[keys[ikey]].alarm.limits.hihi) tray[keys[ikey]].alarm.value = 4;
                }
                let cubeTitle = tray.type + '/' + tray.name + '/' + keys[ikey];
                let index = -1;
                if (alarmList.length > 0)
                {
                    index = alarmList.findIndex(cube => cube.title == cubeTitle);
                }
                if (tray[keys[ikey]].alarm.value < 1)
                {
                    if (index >= 0)
                    {
                        alarmList.splice(index, 1);
//                        node.warn(alarmList);
                    }
                }
                else
                {
                    if (index >= 0)
                    {
                        alarmList[index].cube = JSON.parse(JSON.stringify(tray[keys[ikey]]));
                    }
                    else
                    {
                        let newAlarm = 
                        {
                            title       :   cubeTitle, 
                            type        :   tray.type, 
                            name        :   tray.name, 
                            cubeName    :   keys[ikey], 
                            cube        :   JSON.parse(JSON.stringify(tray[keys[ikey]]))
                        };
                        alarmList.push(newAlarm);
                        newAlarmList.push(newAlarm);
//                        node.warn(alarmList);
                    }
                }
            }
        }
    }
}
if (newAlarmList.length < 1) return null;
return {topic:"newAlarmList", payload:newAlarmList};

flow.set('alarmList',[]);
flow.set('databaseReadFinished',false);
return null;

let alarmHistory = context.get('alarmHistory');
let staleSMSTimeout = Number(env.get("ALARM_TIMEOUT"));
let now = new Date().getTime();
if (alarmHistory.length > 0)
{
    while (alarmHistory.length > 0)
    {
        if ((now - alarmHistory[0].timestamp) > staleSMSTimeout)
        {
            alarmHistory.shift();
        }
        else
        {
            break;
        }
    }

    if (alarmHistory.length > 0)
    {
        if (alarmHistory.findIndex(cube => cube.title == msg.payload.title) >= 0) return null;
    }
}
alarmHistory.push({title:msg.payload.title, timestamp:now});

let type = '';
if (msg.payload.cube.alarm.value == 1) type = 'LOW';
if (msg.payload.cube.alarm.value == 2) type = 'HIGH';
if (msg.payload.cube.alarm.value == 3) type = 'LOLO';
if (msg.payload.cube.alarm.value == 4) type = 'HIHI';
var message = 'Alarm-' + type + ': ' + msg.payload.title;
message = message + ' is at ' + msg.payload.cube.value.toString() + ' ' + msg.payload.cube.unit
let alarmTopic = env.get("BOX") + "/" + env.get("HUB") + "/alarm";
return {topic:alarmTopic, payload:{"type":"message","content":message,"topic":msg.payload.title}};

