function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}

let trayList = []
let viewAllTrays = false;
for (let allowedTray of msg.token.trays) 
{
    if (allowedTray.type == '*') 
    {
        viewAllTrays = true;
        break;
    }
}
for( let ii in msg.payload ) 
{
    if( msg.payload.hasOwnProperty(ii) ) 
    {
        let trayDb = msg.payload[ii];
        if (viewAllTrays)
        {
            trayList.push(JSON.parse(JSON.stringify(trayDb)));
        }
        else
        {
            for (let allowedTray of msg.token.trays) 
            {
                if (allowedTray.type == trayDb.type) 
                {
                    if (allowedTray.name == '*')
                    {
                        trayList.push(JSON.parse(JSON.stringify(trayDb)));
                    }
                    else
                    {
                        if (allowedTray.name == trayDb.name)
                        {
                            trayList.push(JSON.parse(JSON.stringify(trayDb)));
                        }
                    }
                }
            }
        }
    }
}

let trayListType = []
let trayListSort = [];
for( let ii in trayList ) 
{
    let index = trayListType.findIndex(tray => tray.type == trayList[ii].type);
    if (index < 0)
    {
        trayListType.push({type:trayList[ii].type});
        trayListSort.push([trayList[ii]]);
    }
    else
    {
        trayListSort[index].push(trayList[ii]);
    }
}
trayListSort.sort( 
    function(a, b) 
    {
        var x = a[0]['type'].toUpperCase(); 
        var y = b[0]['type'].toUpperCase();
        return ((x < y) ? -1 : ((x > y) ? 1 : 0));
    }
);
for( let ii in trayListSort ) 
{
