template:
// Global Javascript
        useWebSockets(true);
        var mqttDevice = null;
        var device = null;
        var pingInterval = 5000;
        var lastPing = 0;

        var restfulDevice = 
        {
            trayType  : '{{{payload.script.trayType}}}',
            trayName  : '{{{payload.script.trayName}}}',
            box       : '{{{payload.script.box}}}'
        };

        var plotConfig = 
        {
            trace1Title : 'Temp (C)',
            trace1Color : '#557a95',
            trace1GridColor : '#beceda',
            plotLength  : 600000,
            width   : '',
            height  :   '500',
            htmlId  :   'historyStripChart'
        };
        let historyStripChart = new StripChart(plotConfig);

        var colorScale = true;
        var enhancementLevel = 0;
        var pgm = null;
        var imageArray = null;
        var imageDelayChanged = false;
        var programStarted = false;

        function onDocumentReady()
        {
            $('#imageDelayButtonId').hide();
        }
        function onWebSocketOpen()
        {
            if (!programStarted)
            {
                programStarted = true;
                sendActionMsg('getLastImage','readDatabase', restfulDevice);
            }
        }
        function onWebSocketClose()
        {

        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'readings':
                    if ((msg.mqttDevice.name == restfulDevice.trayName) && (msg.mqttDevice.type == restfulDevice.trayType)  && (msg.mqttDevice.box == restfulDevice.box) )
                    {
                        lastPing = new Date().getTime();
                        mqttDevice = msg.mqttDevice;
                        updateDmaReadings();
                    }
                    break;
                case 'getLastImage':
                    if (msg.userID == userID)
                    {
                        device = msg.payload;
                        if (device.imageData.value != null)
                        {
                            $('#snapshotTimeStamp').text(new Date(device.timeStamp).toLocaleString('en-SE', { timeZone: 'Europe/Stockholm' }));
                            pgm = JSON.parse(JSON.stringify(device.imageData.value));
                            imageArray = pgm.imageArray;
                            $('#sensorName').text('Sensor ' + device.name);
                            if (device.transpose.value == 0)
                            {
                                $('#flirCanvas').attr('width', '800');
                            }
                            else
                            {
                                $('#flirCanvas').attr('width', '450');
                            }
                            plotImage();
                        }
                    }
                    break;
                case 'readDatabase':
                    if (msg.userID == userID)
                    {
                    }
                    break;
                case 'renew':
                    if (msg.userID == userID)
                    {
                        updateCookie(msg);
                    }
                    break;
                case 'permissionError':
                    if (msg.userID == userID)
                    {
                        acknowledgeDialog('Permission', 'Error', msg.payload);
                    }
                    break;
                case 'loginExpired':
                    if (msg.userID == userID)
                    {
                       location.reload();
                    }
                    break;
                default:
                    break;
            }
        }
        function sendSetting()
        {
            var actionMsg = 
                {
                    topic   : mqttDevice.box + '/' +  mqttDevice.trayType + '/' +  mqttDevice.name + '/setting',
                    payload : pako.deflate(JSON.stringify(device))
                };
            sendActionMsg('setting','setting',actionMsg);
       }
        function setScalarValue(settingValue, attrName)
        {
            var settingAttr = device[attrName];
            if ( (settingAttr.alarm.limits.lolo <= settingValue) && (settingValue <= settingAttr.alarm.limits.hihi) )
            {
                settingAttr.value = settingValue;
                sendSetting();
            } 
            else
            {
                acknowledgeDialog('Acknowledge', 'Error', 'Entry outside range! (' + settingAttr.alarm.limits.lolo.toString() + '-' + settingAttr.alarm.limits.hihi.toString() + ' ' + settingAttr.unit);
            }
        }
        function reset(actionFlag)
        {
            var headerText = 'Warning';
            var title = 'Resetting Tray';
            var text = 'I hope you know what you are doing.';
            var buttonTexts = ['Reset', 'Cancel'];
            var buttonFunctions = 
            [
                function()
                {
                    $( this ).dialog( "close" );
                    setScalarValue(actionFlag, 'reset');
                 }, 
                function()
                {
                    $( this ).dialog( "close" );
                }
            ];
            if (actionFlag == 2)
            {
                title = 'Rebooting Tray';
                buttonTexts[0] = 'Reboot';
            }
            optionDialog(headerText, title, text, buttonTexts, buttonFunctions);
        }

