        function getCreds()
        {
            var now = new Date().getTime();
            var expDate = Number(getCookie('ExpDate'));
            if (expDate > 0)
            {
                expDate = expDate - now;
                if(expDate < 0)
                {
                    logout();
                }
                else
                {
                    var hours = Math.floor(expDate / 3600000);
                    var minutes = expDate - hours * 3600000;
                    minutes = Math.floor(minutes / 60000);
                    var seconds = expDate - (hours * 3600000) - (minutes * 60000);
                    seconds = Math.floor(seconds / 1000);
                    hours = hours.toString();
                    if (hours.length == 1) hours = '0' + hours; 
                    minutes = minutes.toString();
                    if (minutes.length == 1) minutes = '0' + minutes; 
                    seconds = seconds.toString();
                    if (seconds.length == 1) seconds = '0' + seconds; 
                    var timeString = hours + ':' + minutes + ':' + seconds;
                    $('#usernameId').text(getCookie('Username'));
                    $('#expTimeId').text(timeString);
                }
            }
            else
            {
                $('#usernameId').text(getCookie('Username'));
                $('#expTimeId').text('Forever');
            }
        }
        function logout()
        {
            document.cookie = project + "Role= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
            document.cookie = project + "ExpDate= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
            document.cookie = project + "Username= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
            window.location.href = "/";
        }
        function renew()
        {
            var actionMsg = {};
            sendActionMsg('renew', 'renew', actionMsg);
        }
        function updateCookie(msg)
        {
            document.cookie = project + "Role=" + msg.role;
            document.cookie = project + "ExpDate=" + msg.expDate;
            document.cookie = project + "Username=" + msg.username;
        }
        function downloadToken()
        {
            downloadString(getCookie("Role"), "text/plain", project + "-token.txt");
        }
// from https://gist.github.com/danallison/3ec9d5314788b337b682
        function downloadString(text, fileType, fileName) 
        {
            var blob = new Blob([text], { type: fileType });

            var a = document.createElement('a');
            a.download = fileName;
            a.href = URL.createObjectURL(blob);
            a.dataset.downloadurl = [fileType, a.download, a.href].join(':');
            a.style.display = "none";
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            setTimeout(function() { URL.revokeObjectURL(a.href); }, 1500);
        }

// Update Readings Javascript
        function updateDmaReadings()
        {
            $('#linkQualityId').text(device.linkQuality.value);
            $('#signalLevelId').text(device.signalLevel.value);
            if (!imageDelayChanged)$('#imageDelayInputId').val(device.imageDelay.value);

            $('#triggerModeInputId').val(device.triggerMode.value);
            if (device.triggerMode.value == 0)
            {
                $('#manualTrigger').show();
            }
            else
            {
                $('#manualTrigger').hide();
            }
            $('#transposeInputId').val(device.transpose.value);
            $('#scaleModeInputId').val(device.scaleMode.value);
            if (device.resMode.value == 1)
            {
                $('#chipResolutionId').text('80x60');
            }
            else
            {
                $('#chipResolutionId').text('160x120');
            }

            $('#sensorName').text('Sensor ' + device.name);
            $('#pixelMinId').text(device.pixelMin.value);
            $('#pixelMaxId').text(device.pixelMax.value);
            if (device.archiveData.value  == 1)
            {
                historyStripChart.addTracePoints(device.pixelMax.value, device.pixelMin.value);
                $('#manualTriggerButton').text('Take Image');
                $('#snapshotTimeStamp').text(new Date().toLocaleString('en-SE', { timeZone: 'Europe/Stockholm' }));
                pgm = JSON.parse(JSON.stringify(device.imageData.value));
                imageArray = pgm.imageArray;
                if (device.transpose.value == 0)
                {
                    $('#flirCanvas').attr('width', '800');
                }
                else
                {
                    $('#flirCanvas').attr('width', '450');
                }
                plotImage();
                var image1dArray = [];
                var ipixel = 0;
                var plow = (pgm.minval - 27315) / 100;
                var phigh = (pgm.maxval - 27315) / 100;
            }

            var buttonColor = 'green';
            var buttonTextColor = 'black';
            var buttonText = "Off";
            if (device.flipHorizontal.value > 0) 
            {
                buttonColor = 'red';
                buttonText = 'On';
            }
            $('#flipHorizontalId').css('color', buttonTextColor); 
            $('#flipHorizontalId').css('background-color', buttonColor); 
            $('#flipHorizontalId').text(buttonText); 

            buttonColor = 'green';
            buttonTextColor = 'black';
            buttonText = "Off";
            if (device.flipVertical.value > 0) 
            {
                buttonColor = 'red';
                buttonText = 'On';
            }
            $('#flipVerticalId').css('color', buttonTextColor); 
            $('#flipVerticalId').css('background-color', buttonColor); 
            $('#flipVerticalId').text(buttonText); 
        }
        function plotImage()
        {
            var rgb = [];
            var plow = (pgm.minval - 27315) / 100;
            var phigh = (pgm.maxval - 27315) / 100;
            var pdelta = phigh - plow;
            for (var ibox = 0; ibox < 9; ++ibox)
            {
                var intensity = ibox / 8.0;
                var pbox = Math.round(((phigh - plow) * intensity + plow) * 10.0) / 10.0;
                $('#box' + (ibox + 1).toString() + 'Label').text(pbox.toString());
                if (enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
                if (enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
                if (enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
                if (enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
                if (enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
                if (colorScale)
                {
                    rgb = getRGB(intensity);
                }
                else
                {
                    rgb[0] = Math.floor(255 * intensity);
                    rgb[1] = Math.floor(255 * intensity);
                    rgb[2] = Math.floor(255 * intensity);
                }
                var style='width:88px;height:50px;background-color:' + 'rgb(' + rgb[0].toString() + ', ' + rgb[1].toString() + ', ' + rgb[2].toString() + ')';
                $('#box' + (ibox + 1).toString()).attr('style',style);
            }
            var canvas = document.getElementById("flirCanvas");
            var canvasContext = canvas.getContext("2d");
            var tipPointer = document.getElementById("tipPointer");
            var tipPointerContext = tipPointer.getContext("2d");
            var tip = document.getElementById("tip");
            var tipContext = tip.getContext("2d");
            tipContext.font = "30px Arial";
            tipContext.fillStyle = "cyan";
            tipPointerContext.fillStyle = "cyan";
            if (colorScale) tipContext.fillStyle = "black";
            if (colorScale) tipPointerContext.fillStyle = "black";
            tipContext.textAlign = "left";

            var canvasWidth  = Number($('#flirCanvas').attr('width'));
            var wrapperWidth = Number($('#wrapper').attr('width'));
            var xOffsetKluge = Math.round((wrapperWidth - canvasWidth) / 2);
            var canvasHeight = Number($('#flirCanvas').attr('height'));
            var rectWidth  = Math.round(canvasWidth  / pgm.ncols);
            var rectHeight = Math.round(canvasHeight / pgm.nrows);
            canvas.onmousemove = function (e) 
            {
                var icol = Math.floor(e.layerX / rectWidth);
                if (icol < 0) icol = 0;
                if (icol >= pgm.ncols) icol = pgm.ncols - 1;
                var irow = Math.floor((canvasHeight - e.layerY) / rectHeight);
                if (irow < 0) irow = 0;
                if (irow >= pgm.nrows) irow = pgm.nrows - 1;
                var temp = Math.floor(100.0 * (pdelta * imageArray[irow][icol] / 256.0 + plow)) / 100;

                tipPointer.style.top = (e.layerY  - tipPointer.height / 2).toString()  + "px";
                tipPointer.style.left = (xOffsetKluge + e.layerX - tipPointer.width / 2).toString() + "px";
                tipPointerContext.clearRect(0, 0, tipPointer.width, tipPointer.height);
                tipPointerContext.fillRect( 0, 0, tipPointer.width, tipPointer.height);

                tip.style.top = (e.layerY - tip.height - tipPointer.height).toString()  + "px";
                tip.style.left = (xOffsetKluge + e.layerX).toString() + "px";
                tipContext.clearRect(0, 0, tip.width, tip.height);
                tipContext.fillText(temp.toString() + ' C', 0, 30);
            };

            for (var irow = 0; irow < pgm.nrows; ++irow)
            {
                for (var icol = 0; icol < pgm.ncols; ++icol)
                {
                    var intensity = imageArray[irow][icol] / 256.0;
                    if (enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
                    if (enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
                    if (enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
                    if (enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
                    if (enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
                    if (colorScale)
                    {
                        rgb = getRGB(intensity);
                        rgb[0] = Math.floor(rgb[0]);
                        rgb[1] = Math.floor(rgb[1]);
                        rgb[2] = Math.floor(rgb[2]);
                    }
                    else
                    {
                        rgb[0] = Math.floor(255 * intensity);
                        rgb[1] = Math.floor(255 * intensity);
                        rgb[2] = Math.floor(255 * intensity);
                    }
                    canvasContext.fillStyle = 'rgb(' + rgb[0].toString() + ', ' + rgb[1].toString() + ', ' + rgb[2].toString() + ')';
/*
                    if (intensity > 0.98) 
                    {
                        canvasContext.fillStyle='magenta';
                        if (colorScale) canvasContext.fillStyle='white';
                    }
*/
                    canvasContext.fillRect(icol * rectWidth, canvasHeight - rectHeight - irow * rectHeight, rectWidth, rectHeight);
                }    
            }
        }
        function mouseLeftCanvas()
        {
            var tip = document.getElementById("tip");
            var tipContext = tip.getContext("2d");
            tipContext.clearRect(0, 0, tip.width, tip.height);
            var tipPointer = document.getElementById("tipPointer");
            var tipPointerContext = tipPointer.getContext("2d");
            tipPointerContext.clearRect(0, 0, tipPointer.width, tipPointer.height);
        }
        function getRGB(intensity)
        {
          var rgb = [0,0,0];
          var rgbCoeff = [
              [  48.6, 349.0,    5.72, -172.0],
              [-164.0, 705.0, -667.0 ,  379.0],
              [ 135.0, 332.0, -976.0,   536.0]];
          var intensityStart = [0.0, 0.3176, 0.0];
          var power = [1.0, 0.0, 0.0, 0.0];
          for (var ii = 1; ii < 4; ++ii) power[ii] = power[ii - 1] * intensity;
          for (var irgb = 0; irgb < 3; ++irgb)
          {
              if (intensity >= intensityStart[irgb])
              {
                  for (var ipow = 0; ipow < 4; ++ipow)
                  {
                      rgb[irgb] = rgb[irgb] + rgbCoeff[irgb][ipow] * power[ipow];
                  }
              }
          }
          return rgb;
        }
        function getRGBRainbow(intensity)
        {
          var rgb = [0,0,0];
          if ( (0 <= intensity) && (intensity <= 0.25) )
          {
            rgb[0] = 0;
            rgb[1] = Math.floor(255 * intensity * 4);    
            rgb[2] = 255;
          }
          if ( (0.25 < intensity) && (intensity <= 0.50) )
          {
            rgb[0] = 0;
            rgb[1] = 255;    
            rgb[2] = Math.floor(255 * (0.50 - intensity) * 4);
          }
          if ( (0.50 < intensity) && (intensity <= 0.75) )
          {
            rgb[0] = Math.floor(255 * (intensity - 0.50) * 4);
            rgb[1] = 255;    
            rgb[2] = 0;
          }
          if ( (0.75 < intensity) && (intensity <= 1.0) )
          {
            rgb[0] = 255;
            rgb[1] = Math.floor(255 * (1.0 - intensity) * 4);    
            rgb[2] = 0;
          }
          return rgb;
        }
        function toggleColorScale()
        {
            colorScale = !colorScale;
            if (colorScale)
            {
                $('#colorScaleButton').text('Color');
            }
            else
            {
                $('#colorScaleButton').text('Gray');
            }
            plotImage();
        }
        function toggleEnhancedScale(deltaEnhanced)
        {
            enhancementLevel = enhancementLevel + deltaEnhanced;
            if (enhancementLevel >  2) enhancementLevel = 2;
            if (enhancementLevel < -2) enhancementLevel = -2;
            $('#enhancementLevel').text(enhancementLevel.toString());
            plotImage();
        }
        function clearImage()
        {
            var canvas = document.getElementById("flirCanvas");
