            } 
            $('#pixelMaxBarPlot').hide();
        }
        function flipHorizontal()
        {
            if (device.flipHorizontal.value > 0)
            {
                device.flipHorizontal.value = 0;
            }
            else
            {
                device.flipHorizontal.value = 1;

            }
            $('#flipHorizontalId').css('color', 'black'); 
            $('#flipHorizontalId').css('background-color', 'grey'); 
            $('#flipHorizontalId').text('?'); 
            sendSetting();
        }
        function flipVertical()
        {
            if (device.flipVertical.value > 0)
            {
                device.flipVertical.value = 0;
            }
            else
            {
                device.flipVertical.value = 1;

            }
            $('#flipVerticalId').css('color', 'black'); 
            $('#flipVerticalId').css('background-color', 'grey'); 
            $('#flipVerticalId').text('?'); 
            sendSetting();
        }
        function imageDelayChange()
        {
            imageDelayChanged = true;
            $('#imageDelayButtonId').show();
        }
        function imageDelay()
        {
            $('#imageDelayButtonId').hide();
            if (isNaN($('#imageDelayInputId').val()))
            {
                imageDelayChanged = false;
                return;
            }
            setScalarValue(Number($('#imageDelayInputId').val()), 'imageDelay');
            imageDelayChanged = false;
        }
        function triggerMode()
        {
            if (isNaN($('#triggerModeInputId').val()))
            {
                 return;
            }
            setScalarValue(Number($('#triggerModeInputId').val()), 'triggerMode');
        }
        function scaleMode()
        {
            if (isNaN($('#scaleModeInputId').val()))
            {
                 return;
            }
            setScalarValue(Number($('#scaleModeInputId').val()), 'scaleMode');
        }
        function manualTrigger()
        {
            if (device.manualTrigger.value == 0)
            {
                setScalarValue(1, 'manualTrigger');
                $('#manualTriggerButton').text('ABORT');
            }
            else
            {
                setScalarValue(0, 'manualTrigger');
                $('#manualTriggerButton').text('Take Image');
            }
        }
        function transpose()
        {
            if (isNaN($('#transposeInputId').val()))
            {
                 return;
            }
            setScalarValue(Number($('#transposeInputId').val()), 'transpose');
        }

