//                        range: [0, 2]

                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: 'Max Temp',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'red',
                        size: 10,
                        symbol: 'circle'
                    }
                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: 'Min Temp',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        size: 10,
                        symbol: 'square'
                    }
                };
            }
            addTracePoints(trace1Pt, trace2Pt)
            {
                var now = new Date().getTime();
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                removeDone = false;
                while(!removeDone)
                {
                    if (this.trace2.x[0] < plotBeginning)
                    {
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2], this.layout);
            }
        }

