            createHeaderLabel(width, text)
            {
                let headCol = document.createElement('td');
                headCol.setAttribute('width',width);
                headCol.setAttribute('align',"center");
                let headColSpan = document.createElement('span');
                headColSpan.setAttribute('class','cubeListChooser-tableHeading');
                headColSpan.innerHTML = text;
                headCol.appendChild(headColSpan);
                return headCol;
            }
            traySelected(itrace)
            {
                this.clearNameOptions(itrace);
                if (this.traySelect[itrace].value > -1)
                {
                    let trayType = this.traySelect[itrace].options[this.traySelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].type = trayType;
                    for (let ii in this.app.trays[this.traySelect[itrace].value])
                    {
                        let trayNameOption = document.createElement('option');
                        trayNameOption.value = ii;
                        trayNameOption.innerHTML = this.app.trays[this.traySelect[itrace].value][ii].name;
                        this.nameSelect[itrace].appendChild(trayNameOption);
                    }
                }

            }
            nameSelected(itrace)
            {
                this.clearPropOptions(itrace);
                let props = ["reading", "setting"];
                if (this.nameSelect[itrace].value > -1)
                {
                   let trayName = this.nameSelect[itrace].options[this.nameSelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].name = trayName;
                    for (let ii in props)
                    {
                        let namePropOption = document.createElement('option');
                        namePropOption.value = ii;
                        namePropOption.innerHTML = props[ii];
                        this.propSelect[itrace].appendChild(namePropOption);
                    }
                }
            }
            propSelected(itrace)
            {
                this.clearCubeOptions(itrace);
                if (this.propSelect[itrace].value > -1)
                {
                    let trayProp = this.propSelect[itrace].options[this.propSelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].prop = trayProp;
                    this.cubeData[itrace].validProp = true;
                    this.app.sendActionMsg('getCubeList','readDatabase',this.cubeData[itrace]);
                }
            }
            setCubeList(dbQuery)
            {
                for (let ii in dbQuery.cubeList)
                {
                    let propCubeOption = document.createElement('option');
                    propCubeOption.value = ii;
                    propCubeOption.innerHTML = dbQuery.cubeList[ii];
                    this.cubeSelect[dbQuery.trace].appendChild(propCubeOption);
                }
            }
            cubeSelected(itrace)
            {
                if (this.cubeSelect[itrace].value > -1)
                {
                    let trayCube = this.cubeSelect[itrace].options[this.cubeSelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].cube = trayCube;
                    this.cubeData[itrace].validCube = true;
                }
                else
                {
                    this.cubeData[itrace].cube = '';
                    this.cubeData[itrace].validCube = false;
                }
                this.cubeValueElement[itrace].style.display = 'none';
                this.cubeValueElement[itrace].setAttribute('value', '');
                this.cubeUnitElement[itrace].style.color = 'black';
                this.cubeUnitElement[itrace].innerHTML = '';
                this.cubeData[itrace].tray = {};
                this.alarmButton[itrace].src="/img/greyLight.png";
            }
            clearNameOptions(itrace)
            {
                this.clearPropOptions(itrace);
                while(this.nameSelect[itrace].firstChild)
                {
                    this.nameSelect[itrace].removeChild(this.nameSelect[itrace].lastChild);
                }
                let cubeRowCol3Widget2 =  document.createElement('option');
                cubeRowCol3Widget2.value = -1;
                cubeRowCol3Widget2.innerHTML = ' ';
                this.nameSelect[itrace].appendChild(cubeRowCol3Widget2);
                this.cubeData[itrace].name = '';
            }
            clearPropOptions(itrace)
            {
                this.clearCubeOptions(itrace);
                while(this.propSelect[itrace].firstChild)
                {
                    this.propSelect[itrace].removeChild(this.propSelect[itrace].lastChild);
                }
                let cubeRowCol4Widget2 =  document.createElement('option');
                cubeRowCol4Widget2.value = -1;
                cubeRowCol4Widget2.innerHTML = ' ';
                this.propSelect[itrace].appendChild(cubeRowCol4Widget2);
                this.cubeData[itrace].validProp = false;
                this.cubeData[itrace].prop = '';
            }
            clearCubeOptions(itrace)
            {
                while(this.cubeSelect[itrace].firstChild)
                {
                    this.cubeSelect[itrace].removeChild(this.cubeSelect[itrace].lastChild);
                }
                let cubeRowCol5Widget2 =  document.createElement('option');
                cubeRowCol5Widget2.value = -1;
                cubeRowCol5Widget2.innerHTML = ' ';
                this.cubeSelect[itrace].appendChild(cubeRowCol5Widget2);
                this.cubeData[itrace].validCube = false;
                this.cubeData[itrace].cube = '';
                this.cubeValueElement[itrace].style.display = 'none';
                this.cubeValueElement[itrace].setAttribute('value', '');
                this.cubeUnitElement[itrace].style.color = 'black';
                this.cubeUnitElement[itrace].innerHTML = '';
                this.cubeData[itrace].tray = {};
                this.alarmButton[itrace].src="/img/greyLight.png";
            }
            getTrayValues()
            {
                let trayList = [];
                let traceCount = 0;
                for (let itrace in this.cubeData)
                {
                    if (this.cubeData[itrace].validCube) 
                    {
                        trayList[traceCount] = { $and: [ {type : this.cubeData[itrace].type}, {name : this.cubeData[itrace].name}]};
                        ++traceCount;
                    }
                }
                if (traceCount < 1) return;
                let queryFilter = { $or: trayList };
                let actionMsg = 
                {
                    queryFilter : queryFilter
                };
                this.app.sendActionMsg('getTrayValues', 'readDatabase', actionMsg);
            }
            setSetTrayValues(dbQuery)
            {
                for (let itrace in this.cubeData)
                {
                    if (this.cubeData[itrace].validCube) 
                    {
                        for (let idBtray in dbQuery.trays)
                        {
                            if (this.cubeData[itrace].type == dbQuery.trays[idBtray].type)
                            {
                                if (this.cubeData[itrace].name == dbQuery.trays[idBtray].name)
                                {
                                    switch(dbQuery.trays[idBtray][this.cubeData[itrace].cube].alarm.value)
                                    {
                                        case 0:
                                            this.alarmButton[itrace].src="/img/greenLight.png";
                                            break;
                                        case 1:
                                            this.alarmButton[itrace].src="/img/blueLight.png";
                                            break;
                                        case 2:
                                            this.alarmButton[itrace].src="/img/yellowLight.png";
                                            break;
                                        case 3:
                                            this.alarmButton[itrace].src="/img/purpleLight.png";
                                            break;
                                        case 4:
                                            this.alarmButton[itrace].src="/img/redLight.png";
                                            break;
                                        default:
                                            this.alarmButton[itrace].src="/img/greyLight.png";
                                            break;

                                    }
                                    if (this.cubeData[itrace].settingInProgress) return;
                                    let cubeValue = dbQuery.trays[idBtray][this.cubeData[itrace].cube].value;
                                    let cubeUnit  = dbQuery.trays[idBtray][this.cubeData[itrace].cube].unit;
                                    this.cubeValueElement[itrace].style.display = '';
                                    this.cubeValueElement[itrace].value = cubeValue.toString();
                                    let valSplit = cubeValue.toString().split(".");
                                    if (valSplit.length > 1)
                                    {
                                        if (valSplit[1].length > 5) valSplit[1] = valSplit[1].substring(0,5)
                                        this.cubeValueElement[itrace].value = valSplit[0] + "." + valSplit[1];
                                    }
                                    this.cubeUnitElement[itrace].innerHTML  = cubeUnit.toString();
                                    this.cubeData[itrace].tray = dbQuery.trays[idBtray];
                                    if (this.cubeData[itrace].prop == 'reading') 
                                    {
                                        this.cubeValueElement[itrace].disabled = true;
                                    }
                                    else
                                    {
                                        let cubey = this.cubeData[itrace].tray[this.cubeData[itrace].cube];
                                        if (cubey.hasOwnProperty("enabled"))
                                        {
                                            if (cubey.enabled == 1)
                                            {
                                                this.cubeValueElement[itrace].disabled = false;
                                            }
                                            else
                                            {
                                                this.cubeValueElement[itrace].disabled = true;
                                            }
                                        }
                                        else
                                        {
                                            this.cubeValueElement[itrace].disabled = false;
                                        }                                       
                                    }
                                }
                            }
                        }
                    }
                }
            }
