function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numTrays = objectLength(msg.payload);
if (numTrays < 1) return null;
var tray = msg.payload[0];
var cubeList = [];
var keys = Object.keys(tray);
for (var ii = 0; ii < keys.length; ++ii)
{
    if (tray[keys[ii]].hasOwnProperty('type'))
    {
        if (tray[keys[ii]].type == 'scalar')
        {
            if (tray[keys[ii]].action == msg.prop)
            {
                cubeList.push(keys[ii]);
            }
        }
    }
}
if (cubeList.length < 1) return null;
return {
