};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic:'getArchiveValues',
        payload:{
            topic           : 'getArchiveValues',
            payload         : {itrace: msg.itrace, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
let trace = [];

for (let ii in records)
{
    trace.push({timeStamp: records[ii].timeStamp, value: records[ii][msg.tray.cube]});
}

return {
    topic:'getArchiveValues',
    payload:{
        topic           : 'getArchiveValues',
        payload         : trace,
        userID          : msg.userID,
    }
};

