        class TimePlotCard 
        {
            constructor(app, config, cubeListChooser)
            {
                this.app = app;
                this.config = config;
                this.cubeListChooser = cubeListChooser;
                this.plotRunning = false;
                this.cubeData = [];
                this.timePlotTraces = [];
            }
            onDocumentReady()
            {
                let containerRowDiv = document.createElement('div');
                containerRowDiv.setAttribute('class', 'row timePlotCard-containerRow');
                document.getElementById('appContent').appendChild(containerRowDiv);

                let containerColDiv = document.createElement('div');
                containerColDiv.setAttribute('class', 'col-md-12');
                containerColDiv.setAttribute('align', 'center');
                containerRowDiv.appendChild(containerColDiv);

                let cardDiv = document.createElement('div');
                cardDiv.setAttribute('class', 'card timePlotCard');
                containerColDiv.appendChild(cardDiv);

                let titleTable = document.createElement('table');
                titleTable.setAttribute('width', '100%');
