                let titleSpan  = document.createElement('span');
                titleSpan.setAttribute('class', 'timePlotCard-title');
                titleSpan.innerHTML = this.config.title;
                titleCol.appendChild(titleSpan);
                titleRow.appendChild(titleCol);
                let buttonCol  = document.createElement('td');
                buttonCol.setAttribute('width', '15%');

                this.displayButton = document.createElement('button');
                this.displayButton.setAttribute('class', 'btn btn-block timePlotCard-title-button');
                this.displayButton.innerHTML = 'Close';
                this.displayButton.onclick = event => {this.toggle()};
                buttonCol.appendChild(this.displayButton);
                titleRow.appendChild(buttonCol);

                this.cardBodyDiv = document.createElement('div');
                this.cardBodyDiv.setAttribute('class', 'timePlotCard-body');
                this.cardBodyDiv.setAttribute('align', 'center');
                let cardBodyTable = document.createElement('table');
                cardBodyTable.setAttribute('width', '100%');

                let cardBodyR1 = document.createElement('tr');
                cardBodyTable.appendChild(cardBodyR1);
                let cardBodyR1C1 = document.createElement('td');
                cardBodyR1C1.setAttribute('width', '100%');
                cardBodyR1.appendChild(cardBodyR1C1);
                let controlButtonTable = document.createElement('table');
                controlButtonTable.setAttribute('width', '100%');
                cardBodyR1C1.appendChild(controlButtonTable);
                let controlButtonTableR1 = document.createElement('tr');
                controlButtonTable.appendChild(controlButtonTableR1);
                let controlButtonTableR1C1 = document.createElement('td');
                controlButtonTableR1.appendChild(controlButtonTableR1C1);
                controlButtonTableR1C1.style.width = "20%"
                let controlButtonTableR1C2 = document.createElement('td');
                controlButtonTableR1.appendChild(controlButtonTableR1C2);
                controlButtonTableR1C2.style.width = "60%"
                controlButtonTableR1C2.style.paddingTop = "10px";
                controlButtonTableR1C2.style.paddingBottom = "10px";
                this.startStopButton = document.createElement('button');
                this.startStopButton.classList.add('btn-primary');
                this.startStopButton.classList.add('btn-block');
                this.startStopButton.classList.add('cubeListChooser-button');
                this.startStopButton.innerHTML = 'Start';
                this.startStopButton.onclick = event => {this.startStopPlot()};
                controlButtonTableR1C2.appendChild(this.startStopButton);

                let controlButtonTableR1C3 = document.createElement('td');
                controlButtonTableR1.appendChild(controlButtonTableR1C3);
                controlButtonTableR1C3.style.width = "20%"

                let cardBodyR2 = document.createElement('tr');
                cardBodyTable.appendChild(cardBodyR2);
                let cardBodyR2C1 = document.createElement('td');
                cardBodyR2C1.setAttribute('width', '100%');
                cardBodyR2.appendChild(cardBodyR2C1);
                this.timePlotChartDiv = document.createElement('div');
                this.timePlotChartDiv.setAttribute('width', '100%');
                this.timePlotChartDiv.setAttribute('id', 'timePlotChart');
                this.timePlotChartDiv.setAttribute('style', 'padding:20px;');
                cardBodyR2C1.appendChild(this.timePlotChartDiv);

                this.cardBodyDiv.appendChild(cardBodyTable);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);

                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }
//                this.setupPlot();
                window.addEventListener("resize", event => {this.resize()});
            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                    if (this.timePlotTraces.length > 0)
                    {
                        this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                        Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    }
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.timePlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            setupPlot()
            {
                this.timePlotTraces = [];
                for (let icube in this.cubeData)
                {
                    let axis = 'y1';
                    if (this.cubeData[icube].traceAxis == 2) axis = 'y2';
                    let modeType = 'lines';
                    if (this.cubeData[icube].tracePts) modeType = 'markers';
                    let trace = 
                    {
                        x: [],
                        y: [],
                        marker: {size: this.config.markerSize},
                        name: axis + ' ' + this.cubeData[icube].type + '.' + this.cubeData[icube].name + '.' + this.cubeData[icube].cube  + ' (' + this.cubeData[icube].tray[this.cubeData[icube].cube].unit + ')',
                        yaxis: axis,
                        type: 'scatter',
                        mode: modeType,
                    };
                    this.timePlotTraces.push(trace);
                }
            }
            addTracePointsToTimePlot()
            {
                if (!this.plotRunning) return;
                let now = new Date().getTime();
                let plotBeginning = now - this.config.timePlotLength;
                let removeDone = false;
                if (this.timePlotTraces.length < 1) return;
                if (this.timePlotTraces[0].x.length < 1) removeDone = true;
                while(!removeDone)
                {
                    if (this.timePlotTraces[0].x[0] < plotBeginning)
                    {
                        for (let ii = 0; ii < this.timePlotTraces.length; ++ii)
                        {
                            this.timePlotTraces[ii].x.shift();
                            this.timePlotTraces[ii].y.shift();
                        }
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                for (let icube in this.cubeData)
                {
                    this.timePlotTraces[icube].x.push(now);
                    this.timePlotTraces[icube].y.push(this.cubeData[icube].tray[this.cubeData[icube].cube].value);
                }
                Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                this.resize();
            }
            startStopPlot()
            {
                if (this.plotRunning)
                {
                    this.plotRunning = false;
                    this.cubeListChooser.timePlotRunning(false);
                    this.startStopButton.innerHTML = 'Start';
                    this.startStopButton.classList.replace('btn-warning', 'btn-primary');
                }
                else
                {
                    let validTrace = false;
                    this.cubeData = [];
                    for (let itrace in this.cubeListChooser.cubeData)
                    {
                        if (this.cubeListChooser.cubeData[itrace].validCube)
                        {
                            if (this.cubeListChooser.cubeData[itrace].traceAxis > 0)
                            {
                                validTrace = true;
                                this.cubeData.push(this.cubeListChooser.cubeData[itrace]);
                            }
                        }
                    }
                    if (validTrace)
                    {
                        this.plotRunning = true;
                        this.cubeListChooser.timePlotRunning(true);
                        this.startStopButton.innerHTML = 'Stop';
                        this.startStopButton.classList.replace('btn-primary', 'btn-warning');
                        this.setupPlot();
                    }
                }
            }
        }

