            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.archivePlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            plotArchive()
            {
                this.archivePlotTraces = [];
                for (let itrace in this.archiveList)
                {
                    if (this.archiveList[itrace].length > 0 )
                    {
                        let axis = 'y1';
                        if (this.cubeData[itrace].traceAxis == 2) axis = 'y2';
                        let modeType = 'lines';
                        if (this.cubeData[itrace].tracePts) modeType = 'markers';
                        let trace =  
                        {
                            x: [],
                            y: [],
                            marker: {size: this.config.markerSize},
                            name: axis + ' ' + this.cubeData[itrace].type + '.' + this.cubeData[itrace].name + '.' + this.cubeData[itrace].cube + ' (' + this.cubeData[itrace].tray[this.cubeData[itrace].cube].unit + ')',
                            yaxis: axis,
                            type: 'scatter',
                            mode: modeType
                        }
                        for (let ipt in this.archiveList[itrace])
                        {
                            trace.x[ipt] = this.archiveList[itrace][ipt].timeStamp;
                            trace.y[ipt] = this.archiveList[itrace][ipt].value;
                        }
                        this.archivePlotTraces.push(trace);
                    }
                }
                if (this.archivePlotTraces.length > 0)
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            getArchiveData()
            {
                let startDate = new Date(this.startDateInputWidget.value).getTime();
                let stopDate  = new Date(this.stopDateInputWidget.value).getTime();
                if ((stopDate - startDate) < 1 ) return;
                let longtimeWarning = this.config.longtimeWarning * 3600000;
                console.log(longtimeWarning);
                if ((stopDate - startDate)  > longtimeWarning)
                {
                    this.app.okCancelDialog.showDialog('Warning: Long time period. Continue?',(isOk) => 
                    {
                        if (isOk) this.prepareArchiveRequest();
                    });
                }
                else
                {
                    this.prepareArchiveRequest();
                }
            }
            prepareArchiveRequest()
            {
                let startDate = new Date(this.startDateInputWidget.value).getTime();
                let stopDate  = new Date(this.stopDateInputWidget.value).getTime();

                let validTrace = false;
                this.cubeData = [];
                for (let itrace in this.cubeListChooser.cubeData)
                {
                    if (this.cubeListChooser.cubeData[itrace].validCube)
                    {
                        if (this.cubeListChooser.cubeData[itrace].traceAxis > 0)
                        {
                            validTrace = true;
                            this.cubeData.push(this.cubeListChooser.cubeData[itrace]);
                        }
                    }
                }
                if (validTrace)
                {
                    this.cubeListChooser.archivePlotRunning(true);
                    this.getDataButtonWidget.disabled = true;
                    this.startDateInputWidget.disabled = true;
                    this.stopDateInputWidget.disabled = true;
                    this.archiveRequestList = [];
                    this.archiveList = [];
            // If we are replacing a previously generated file we need to
            // manually revoke the object URL to avoid memory leaks.
                    for (let ii in this.csvFile)
                    {
                        if (this.csvFile[ii] !== null) 
                        {
                            window.URL.revokeObjectURL(this.csvFile[ii]);
                        }
                    }
                    this.csvFile = [];


                    this.archiveRequestList = [];
                    this.archiveList = [];

                    if (this.csvLinkTable.hasChildNodes())
                    {
                        while (this.csvLinkTable.firstChild) 
                        {
                            let csvLinkTableR = this.csvLinkTable.lastChild;
                            while(csvLinkTableR.firstChild)
                            {
                                let csvLinkTableRC = csvLinkTableR.lastChild;
                                while (csvLinkTableRC.firstChild)
                                {
                                    csvLinkTableRC.removeChild(csvLinkTableRC.lastChild);
                                }
                                csvLinkTableR.removeChild(csvLinkTableR.lastChild);
                            }
                            this.csvLinkTable.removeChild(this.csvLinkTable.lastChild);
                        }
                    }

                    for (let itrace in this.cubeData)
                    {
                        this.archiveRequestList[itrace] = 
                        {
                            type : this.cubeData[itrace].type,
                            name : this.cubeData[itrace].name, 
                            cube : this.cubeData[itrace].cube,
                            itrace: itrace,
                            startDate: startDate, 
                            stopDate: stopDate,
                        };

                    }
                    this.getArchiveValues();
                }
            }
            printCsvTable()
            {
                for (let itrace in this.archiveList)
                {
                    if (this.archiveList[itrace].length > 0 )
                    {
                        let csvLinkTableR = document.createElement('tr');
                        this.csvLinkTable.appendChild(csvLinkTableR);
                        let csvLinkTableRC = document.createElement('td');
                        csvLinkTableR.appendChild(csvLinkTableRC);
                        csvLinkTableRC.style.width = "100%";

                        let csvLinkTableRCA =  document.createElement('a');
                        let href = this.makeCsvFile(itrace);
                        this.csvFile.push(href);
                        csvLinkTableRCA.href = href;
                        csvLinkTableRCA.target = '_blank';
                        csvLinkTableRCA.download = this.cubeData[itrace].type + '_' + this.cubeData[itrace].name + '_' + this.cubeData[itrace].cube + '.csv';
                        csvLinkTableRCA.classList.add('cubeListChooser-csv-linked');
                        csvLinkTableRCA.style.textDecoration = 'underline';
                        csvLinkTableRCA.innerHTML = this.cubeData[itrace].type + '_' + this.cubeData[itrace].name + '_' + this.cubeData[itrace].cube + '.csv';
                        csvLinkTableRC.appendChild(csvLinkTableRCA);
                    }
                }

            }
            makeCsvFile(itrace)
            {
                let dataString = '';
                let startT = this.archiveList[itrace][0].timeStamp;
                let startTString = new Date(startT).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', '');


                dataString = dataString + 'Tray,' + this.cubeData[itrace].type + ',' + this.cubeData[itrace].name + ',' + this.cubeData[itrace].cube  +'\n';
                dataString = dataString + 'StartDate,' + startTString + '\n';
                dataString = dataString + 'StartDate (mS),' + startT.toString() + '\n';
                dataString = dataString + 'Time (sec), Value (' + this.cubeData[itrace].tray[this.cubeData[itrace].cube].unit + ')\n';
                for (let ipt in  this.archiveList[itrace])
                {
                    let x = (this.archiveList[itrace][ipt].timeStamp - startT) / 1000;
                    let y = this.archiveList[itrace][ipt].value;
                    dataString = dataString + x.toString() + ',' + y.toString() + '\n';
                }
                let data = new Blob([dataString], {type: 'text/plain'});
                return window.URL.createObjectURL(data);
            }

        }

