                cardBodyTable.appendChild(cardBodyR2);
                let cardBodyR2C1 = document.createElement('td');
                cardBodyR2C1.setAttribute('width', '100%');
                cardBodyR2.appendChild(cardBodyR2C1);
                this.xyPlotChartDiv = document.createElement('div');
                this.xyPlotChartDiv.setAttribute('width', '100%');
                this.xyPlotChartDiv.setAttribute('id', 'xyPlotChart');
                this.xyPlotChartDiv.setAttribute('style', 'padding:20px;');
                cardBodyR2C1.appendChild(this.xyPlotChartDiv);

                this.cardBodyDiv.appendChild(cardBodyTable);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);

                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }
//                this.setupPlot();
                window.addEventListener("resize", event => {this.resize()});
            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                    if (this.xyPlotTraces.length > 0)
                    {
                        this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                        Plotly.newPlot('xyPlotChart', this.xyPlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    }
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.xyPlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    let traceData = [];
                    for (let itrace in this.xyPlotTraces)
                    {
                        traceData.push(this.xyPlotTraces[itrace]);
                        traceData.push(this.xyPlotLastPoint[itrace]);
                    }
                    Plotly.newPlot('xyPlotChart', traceData, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            setupPlot()
            {
                let defaultColors = Plotly.d3.scale.category10().range();
                let defaultColorIndex = 0;
                this.xyPlotTraces = [];
                this.xyPlotLastPoint = [];
                this.config.plotlyLayout.xaxis.title = this.cubeData[0].type + '.' + this.cubeData[0].name + '.' + this.cubeData[0].cube  + ' (' + this.cubeData[0].tray[this.cubeData[0].cube].unit + ')';
                for (let icube in this.cubeData)
                {
                    if (Number(icube)> 0)
                    {
                        let axis = 'y1';
                        if (this.cubeData[icube].traceAxis == 2) axis = 'y2';
                        let modeType = 'markers';
                        let color = getHexColor(defaultColors[defaultColorIndex]);
                        defaultColorIndex = defaultColorIndex + 1;
                        if (defaultColorIndex > 9) defaultColorIndex = 0;
                        let trace = 
                        {
                            x: [],
                            y: [],
                            marker: {size: this.config.markerSize, line: {color:color, width: 2}, symbol: 'circle'},
                            name: axis + ' ' + this.cubeData[icube].type + '.' + this.cubeData[icube].name + '.' + this.cubeData[icube].cube  + ' (' + this.cubeData[icube].tray[this.cubeData[icube].cube].unit + ')',
                            yaxis: axis,
                            type: 'scatter',
                            mode: modeType,
                            line: {color: (color + "00")}
                        };
                        this.xyPlotTraces.push(trace);
                        let traceLastPt = 
                        {
                            x: [],
                            y: [],
                            name:  axis + ' ' + this.cubeData[icube].type + '.' + this.cubeData[icube].name + '.' + this.cubeData[icube].cube  + ' (' + this.cubeData[icube].tray[this.cubeData[icube].cube].unit + ')',
                            yaxis: axis,
                            type: 'scatter',
                            mode: 'markers',
                            marker: { size: this.config.markerSize, symbol: 'circle'},
                            line: {color: color},
                            showlegend: false
                        };
                        this.xyPlotLastPoint.push(traceLastPt);
                    }
                }
            }
            addTracePointsToXyPlot()
            {
                if (!this.plotRunning) return;
                let removeDone = false;
                if (this.xyPlotTraces.length < 1) return;
                if (this.xyPlotTraces[0].x.length < 1) removeDone = true;
                while(!removeDone)
                {
                    if (this.xyPlotTraces[0].x.length > this.config.numPoints)
                    {
                        for (let ii = 0; ii < this.xyPlotTraces.length; ++ii)
                        {
                            this.xyPlotTraces[ii].x.shift();
                            this.xyPlotTraces[ii].y.shift();
                        }
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                let xvalue = this.cubeData[0].tray[this.cubeData[0].cube].value
                for (let icount in this.cubeData)
                {
                    let icubeData  = Number(icount);

                    if (icubeData > 0)
                    {
                        if (this.xyPlotLastPoint[icubeData - 1].x.length >= 1)
                        {
                            this.xyPlotTraces[icubeData - 1].x.push(this.xyPlotLastPoint[icubeData - 1].x[0]);
                            this.xyPlotTraces[icubeData - 1].y.push(this.xyPlotLastPoint[icubeData - 1].y[0]);
                        }
                        this.xyPlotLastPoint[icubeData - 1].x[0] = xvalue;
                        this.xyPlotLastPoint[icubeData - 1].y[0] = this.cubeData[icubeData].tray[this.cubeData[icubeData].cube].value;
                    }
                }
                let traceData = [];
                for (let itrace in this.xyPlotTraces)
                {
                    traceData.push(this.xyPlotTraces[itrace]);
                    traceData.push(this.xyPlotLastPoint[itrace]);
                }
                Plotly.newPlot('xyPlotChart', traceData, this.config.plotlyLayout, this.config.plotlyDisplay);
                this.resize();
            }
            startStopPlot()
            {
                if (this.plotRunning)
                {
                    this.plotRunning = false;
                    this.cubeListChooser.xyPlotRunning(false);
                    this.startStopButton.innerHTML = 'Start';
                    this.startStopButton.classList.replace('btn-warning', 'btn-primary');
                }
                else
                {
                    let validTrace = false;
                    this.cubeData = [];
                    for (let itrace in this.cubeListChooser.cubeData)
                    {
                        if (this.cubeListChooser.cubeData[itrace].validCube)
                        {
                            if (this.cubeListChooser.cubeData[itrace].traceAxis > 0)
                            {
                                validTrace = true;
                                this.cubeData.push(this.cubeListChooser.cubeData[itrace]);
                            }
                        }
                    }
                    if (validTrace && (this.cubeData.length > 1))
                    {
                        this.plotRunning = true;
                        this.cubeListChooser.xyPlotRunning(true);
                        this.startStopButton.innerHTML = 'Stop';
                        this.startStopButton.classList.replace('btn-primary', 'btn-warning');
                        this.setupPlot();
                    }
                }
            }
        }

