                this.displayButton.innerHTML = 'Close';
                this.displayButton.onclick = event => {this.toggle()};
                buttonCol.appendChild(this.displayButton);
                titleRow.appendChild(buttonCol);

                this.cardBodyDiv = document.createElement('div');
                this.cardBodyDiv.setAttribute('class', 'cubeListChooser-body');
                this.cardBodyDiv.setAttribute('align', 'center');
                this.cardBodyTable = document.createElement('table');
                this.cardBodyTable.setAttribute('width', '100%');

                this.createHeaderRow();

                this.addTraceRow();

                this.cardBodyDiv.appendChild(this.cardBodyTable);

                let addRowButtonDiv = document.createElement('div');
                this.cardBodyDiv.appendChild(addRowButtonDiv);
                addRowButtonDiv.style.paddingTop = "10px";
                addRowButtonDiv.style.paddingBottom = "10px";

                let addRowButtonTable = document.createElement('table');
                addRowButtonDiv.appendChild(addRowButtonTable);
                addRowButtonTable.setAttribute('width', '100%');
                let addRowButtonTableR1 = document.createElement('tr');
                addRowButtonTable.appendChild(addRowButtonTableR1);
                let addRowButtonTableR1C1 = document.createElement('td');
                addRowButtonTableR1.appendChild(addRowButtonTableR1C1);
                addRowButtonTableR1C1.setAttribute('width', '33%');
                let addRowButtonTableR1C2 = document.createElement('td');
                addRowButtonTableR1.appendChild(addRowButtonTableR1C2);
                addRowButtonTableR1C2.setAttribute('width', '33%');
                let addRowButtonTableR1C3 = document.createElement('td');
                addRowButtonTableR1.appendChild(addRowButtonTableR1C3);
                addRowButtonTableR1C3.setAttribute('width', '33%');

                this.saveConfigButton = document.createElement('button');
                this.saveConfigButton.setAttribute('class', 'btn-primary btn-block cubeListChooser-button');
                this.saveConfigButton.innerHTML = 'Save Config';
                this.saveConfigButton.onclick = event => {this.changePicobus(0)};
                this.saveConfigButton.style.display = 'none';
                addRowButtonTableR1C3.appendChild(this.saveConfigButton);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);
                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }
                setInterval(()=>this.getTrayValues(),this.config.dataUpdatePeriodmS);

            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            createHeaderLabel(width, text)
            {
                let headCol = document.createElement('td');
                headCol.setAttribute('width',width);
                headCol.setAttribute('align',"center");
                let headColSpan = document.createElement('span');
                headColSpan.setAttribute('class','cubeListChooser-tableHeading');
                headColSpan.innerHTML = text;
                headCol.appendChild(headColSpan);
                return headCol;
            }
            traySelected(itrace)
            {
                this.clearNameOptions(itrace);
                if (this.traySelect[itrace].value > -1)
                {
                    let trayType = this.traySelect[itrace].options[this.traySelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].type = trayType;
                    for (let ii in this.app.trays[this.traySelect[itrace].value])
                    {
                        let trayNameOption = document.createElement('option');
                        trayNameOption.value = ii;
                        trayNameOption.innerHTML = this.app.trays[this.traySelect[itrace].value][ii].name;
                        this.nameSelect[itrace].appendChild(trayNameOption);
                    }
                }

            }
            nameSelected(itrace)
            {
                this.clearPropOptions(itrace);
                let props = ["reading", "setting"];
                if (this.nameSelect[itrace].value > -1)
                {
                   let trayName = this.nameSelect[itrace].options[this.nameSelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].name = trayName;
                    for (let ii in props)
                    {
                        let namePropOption = document.createElement('option');
                        namePropOption.value = ii;
                        namePropOption.innerHTML = props[ii];
                        this.propSelect[itrace].appendChild(namePropOption);
                    }
                }
            }
            propSelected(itrace)
            {
                this.clearCubeOptions(itrace);
                if (this.propSelect[itrace].value > -1)
                {
                    let trayProp = this.propSelect[itrace].options[this.propSelect[itrace].selectedIndex].text;
                    this.cubeData[itrace].prop = trayProp;
                    this.cubeData[itrace].validProp = true;
                    this.app.sendActionMsg('getCubeList','readDatabase',this.cubeData[itrace]);
                }
            }
            setCubeList(dbQuery)
            {
                for (let ii in dbQuery.cubeList)
                {
