            setSetTrayValues(dbQuery)
            {
                for (let itrace in this.cubeData)
                {
                    if (this.cubeData[itrace].validCube) 
                    {
                        for (let idBtray in dbQuery.trays)
                        {
                            if (this.cubeData[itrace].type == dbQuery.trays[idBtray].type)
                            {
                                if (this.cubeData[itrace].name == dbQuery.trays[idBtray].name)
                                {
                                    switch(dbQuery.trays[idBtray][this.cubeData[itrace].cube].alarm.value)
