        payload:{
            topic           : 'getArchiveValues',
            payload         : {itrace: msg.itrace, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
var trace = [];
var maxPts = msg.maxPtsToPlot;
var nstep = numRecords / msg.maxPtsToPlot;
if (nstep < 1.0) 
{
    nstep = 1.0;
    maxPts = numRecords;
}
var icnt = 0;

while(icnt < maxPts)
{
    var index = Math.round(icnt * nstep);
    trace[icnt] = {timeStamp: records[index].timeStamp, value: records[index][msg.tray.attr]};
    ++icnt
}

return {
    topic:'getArchiveValues',
    payload:{
        topic           : 'getArchiveValues',
        payload         : {itrace: msg.itrace, trace:trace},
        userID          : msg.userID,
    }
};

var newMsg = 
{
    topic           : 'getNameList',
    userID          : msg.userID,
    payload         : [{type:msg.payload.type},{projection:{name:1, _id:0}}],
    type            : msg.payload.type,
    trace           : msg.payload.trace
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numNames = objectLength(msg.payload);
var nameList = [];

for (var iname = 0; iname < numNames; ++iname)
{
    nameList[iname] = msg.payload[iname].name;
}
return {
    topic:'getNameList',
    payload:{
        topic           : 'getNameList',
        nameList        : nameList,
        userID          : msg.userID,
        type            : msg.type,
        trace           : msg.trace,
    }
};

