    {
        if ((trayInfo[itrace].type.length > 0) && (trayInfo[itrace].name.length > 0)) 
        {
            trayList[traceCount] = { $and: [ {type : trayInfo[itrace].type}, {name : trayInfo[itrace].name}]};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: trayList };
