            showDialog(record)
            {
                if (record == null)
                {
                    this.editTitleInput.value = "";
                    this.logEntryTextArea.value = "";
                    this.record = null;
                }
                else
                {
                    this.editTitleInput.value = record.title;
                    this.logEntryTextArea.value = record.text;
                    this.record = record;
                }
                this.cardContainer.style.display = "block";
            }
            closeDialog()
            {
                this.cardContainer.style.display = "none";
            }
            okButtonPressed(okchoice)
            {
                if (okchoice)
                {
                    if (this.record == null)
                    {
                        let entry = 
                        {
                            timeStamp : new Date().getTime(),
                            author    : '',
                            title     : this.editTitleInput.value,
                            text      : this.logEntryTextArea.value
                        };
                        let actionMsg = 
                        {
                            topic   : 'addLogBookEntry',
                            payload : entry
                        };
                        this.app.sendActionMsg('addLogBookEntry','writeDatabase',actionMsg);
                    }
                    else
                    {
                        let entry = 
                        {
                            timeStamp : new Date().getTime(),
                            origTimeStamp : this.record.timeStamp,
                            author    : this.record.author,
                            title     : this.editTitleInput.value,
                            text      : this.logEntryTextArea.value
                        };

                        let actionMsg = 
                        {
                            topic   : 'editLogBookEntry',
                            payload : entry
                        };
                        this.app.sendActionMsg('editLogBookEntry','writeDatabase',actionMsg);
                    }
                }
                this.cardContainer.style.display = "none";
            }
        }

