                this.stopDateInputWidget =  document.createElement('input');
                this.stopDateInputWidget.classList.add("cubeListChooser-inputbox");
                this.stopDateInputWidget.id = 'archiveStopDateInput';
                this.stopDateInputWidget.type = 'text';
                this.setStopString(stopTime);
                controlButtonTableR1C4.appendChild(this.stopDateInputWidget);

                let controlButtonTableR1C5 = document.createElement('td');
                controlButtonTableR1.appendChild(controlButtonTableR1C5);
                controlButtonTableR1C5.style.width = "20%";
                controlButtonTableR1C5.style.textAlign = "right";

                this.getDataButtonWidget =  document.createElement('button');
                this.getDataButtonWidget.classList.add('btn-primary');
                this.getDataButtonWidget.classList.add('cubeListChooser-button');
                this.getDataButtonWidget.innerHTML = 'Get Data';
                this.getDataButtonWidget.onclick = event => {this.getArchiveData()};
                controlButtonTableR1C5.appendChild(this.getDataButtonWidget);


                let controlButtonTableR2 = document.createElement('tr');
                controlButtonTable.appendChild(controlButtonTableR2);

                let controlButtonTableR2C1 = document.createElement('td');
                controlButtonTableR2.appendChild(controlButtonTableR2C1);
                controlButtonTableR2C1.style.width = "10%";
                controlButtonTableR2C1.style.textAlign = "right";

                let authorLabel = document.createElement('span');
                controlButtonTableR2C1.appendChild(authorLabel);
                authorLabel.classList.add("cubeListChooser-tableHeading");
                authorLabel.innerHTML = "Author"

                let controlButtonTableR2C2 = document.createElement('td');
                controlButtonTableR2.appendChild(controlButtonTableR2C2);
                controlButtonTableR2C2.style.width = "30%";

                this.authorInput =  document.createElement('input');
                this.authorInput.classList.add("cubeListChooser-inputbox");
                this.authorInput.type = 'text';
                this.authorInput.value = "";
                controlButtonTableR2C2.appendChild(this.authorInput);

                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }
            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = 'Close';
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = 'Open';
                }

            }
            getArchiveData()
            {
                let startTime = new Date(this.startDateInputWidget.value).getTime();
                let stopTime  = new Date(this.stopDateInputWidget.value).getTime();
                if ((stopTime - startTime) < 1 ) return;
                let actionMsg = 
                {
                    topic   : 'getLogBook',
                    payload : {startTime: startTime, stopTime: stopTime, author: this.authorInput.value.trim()}
                };
                this.app.sendActionMsg('getLogBook','readDatabase',actionMsg);
            }
            setStartString(startTime)
            {
                this.startDateInputWidget.value = new Date(startTime).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', '');
            }
            setStopString(stopTime)
            {
                this.stopDateInputWidget.value = new Date(stopTime).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', '');
            }

        }

